        long long max_full = kadane(a, 0, n-1);

        if (max_full != total) {
            cout << (total > max_full ? "YES\n" : "NO\n");
        } else {
            long long left = kadane(a, 1, n-1);
            long long right = kadane(a, 0, n-2);
            long long max_adel = max(left, right);

            cout << (total > max_adel ? "YES\n" : "NO\n");
        }
    }

    return 0;
}

