using ll = long long;

int highest_bit(ll x) {
    return x == 0 ? -1 : 63 - __builtin_clzll(x);
}

void solve() {
    int n;
    cin >> n;
    vector<ll> a(n + 1);
    vector<ll> prefix(n + 1, 0);
    for (int i = 1; i <= n; ++i) {
        cin >> a[i];
        prefix[i] = prefix[i - 1] ^ a[i];
    }
    ll S = prefix[n];
    if (S == 0) {
        cout << string(n, '1') << '\n';
        return;
    }
    int h = highest_bit(S);
    vector<bool> left_possible(n + 2, false);
    vector<bool> right_possible(n + 2, false);
    vector<bool> has_lower(n + 2, false);
    bool has = false;
    for (int k = n; k >= 1; --k) {
        if (highest_bit(prefix[k]) == h) {
            has = true;
        }
        left_possible[k] = has;
    }
    has = false;
    for (int k = 0; k <= n; ++k) {
        if (k > 0) {
            has_lower[k] = has_lower[k - 1];
        }
        if (highest_bit(prefix[k]) < h) {
            has_lower[k] = true;
        }
    }
    string res(n, '0');
    for (int i = 1; i <= n; ++i) {
        if (highest_bit(a[i]) != h) {
            continue;
        }
        bool ok = left_possible[i];
        if (i > 1) {
            ok = ok || has_lower[i - 1];
        }
        if (ok) {
            res[i - 1] = '1';
        }
    }
    cout << res << '\n';
}

