vector<int> H_global;

void init(int N, vector<int> H) {
    H_global = H;
}

int max_towers(int L, int R, int D) {
    vector<int> towers;
    for (int i = L; i <= R; ++i) {
        towers.push_back(i);
    }
    int S = towers.size();
    if (S == 0) return 0;

    for (int K = S; K >= 1; --K) {
        vector<bool> mask(S, false);
        fill(mask.end() - K, mask.end(), true);
        do {
            vector<int> subset;
            for (int i = 0; i < S; ++i) {
                if (mask[i]) {
                    subset.push_back(towers[i]);
                }
            }
            sort(subset.begin(), subset.end());
            bool valid = true;
            for (int i = 0; i < subset.size() && valid; ++i) {
                for (int j = i + 1; j < subset.size(); ++j) {
                    int a = subset[i], b = subset[j];
                    bool found = false;
                    for (int k = a + 1; k < b; ++k) {
                        if (H_global[k] >= max(H_global[a], H_global[b]) + D) {
                            found = true;
                            break;
                        }
                    }
                    if (!found) {
                        valid = false;
                        break;
                    }
                }
            }
            if (valid) {
                return K;
            }
        } while (next_permutation(mask.begin(), mask.end()));
    }
    return 1;
}

#include <bits/stdc++.h>
#include "supertrees.h"
#include <vector>
#include <numeric>
using namespace std;

struct DSU {
    vector<int> parent;
    DSU(int n) : parent(n) {
        iota(parent.begin(), parent.end(), 0);
    }
    int find(int u) {
        if (parent[u] != u) parent[u] = find(parent[u]);
        return parent[u];
    }
    void unite(int u, int v) {
        parent[find(u)] = find(v);
    }
};

int construct(vector<vector<int>> p) {
    int n = p.size();
    DSU dsu(n);
    // Build DSU based on p[i][j] > 0
    for (int i = 0; i < n; ++i) {
        for (int j = 0; j < n; ++j) {
            if (p[i][j] > 0) {
                dsu.unite(i, j);
            }
        }
    }
    // Verify that p is consistent with DSU
    for (int i = 0; i < n; ++i) {
        for (int j = 0; j < n; ++j) {
            if (p[i][j] > 0 && dsu.find(i) != dsu.find(j)) {
                return 0;
            }
            if (p[i][j] == 0 && dsu.find(i) == dsu.find(j)) {
                return 0;
            }
        }
    }
    // Collect components
    vector<vector<int>> components(n);
    for (int i = 0; i < n; ++i) {
        components[dsu.find(i)].push_back(i);
    }
    vector<vector<int>> answer(n, vector<int>(n, 0));
    for (auto &comp : components) {
        if (comp.empty()) continue;
        int m = comp.size();
        // Check all p[i][j] are 1 or 2
        bool has_three = false;
        for (int i : comp) {
            for (int j : comp) {
                if (p[i][j] < 0 || p[i][j] > 2) {
                    return 0;
                }
            }
        }
        // Check if all are 2
        bool all_two = true;
        for (int i : comp) {
            for (int j : comp) {
                if (i != j && p[i][j] != 2) {
                    all_two = false;
                    break;
                }
            }
            if (!all_two) break;
        }
        if (all_two) {
            if (m < 3) {
                return 0;
            }
            for (int k = 0; k < m; ++k) {
                int u = comp[k];
                int v = comp[(k+1) % m];
                answer[u][v] = 1;
                answer[v][u] = 1;
            }
        } else {
            int root = -1;
            for (int candidate : comp) {
                bool valid = true;
                for (int other : comp) {
                    if (p[candidate][other] != 1) {
                        valid = false;
                        break;
                    }
                }
                if (valid) {
                    root = candidate;
                    break;
                }
            }
            if (root == -1) {
                return 0;
            }
            vector<int> non_root;
            for (int node : comp) {
                if (node != root) {
                    non_root.push_back(node);
                }
            }
            int k = non_root.size();
            for (int i = 0; i < k; ++i) {
                for (int j = i+1; j < k; ++j) {
                    if (p[non_root[i]][non_root[j]] != 2) {
                        return 0;
                    }
                }
            }
            for (int node : non_root) {
                answer[root][node] = 1;
                answer[node][root] = 1;
            }
            if (k >= 2) {
                for (int i = 0; i < k; ++i) {
                    int u = non_root[i];
                    int v = non_root[(i+1) % k];
                    answer[u][v] = 1;
                    answer[v][u] = 1;
                }
            }
        }
    }
    build(answer);
    return 1;
}

