#include <bits/stdc++.h>
using namespace std;

using ll = long long;
const ll INF = 1e18;

struct Dinic {
    struct Edge {
        int to, rev;
        ll cap;
    };

    int n;
    vector<vector<Edge>> g;
    vector<int> level, ptr;

    Dinic(int n) : n(n), g(n+1), level(n+1), ptr(n+1) {}

    void add_edge(int u, int v, ll cap) {
        g[u].push_back({v, (int)g[v].size(), cap});
        g[v].push_back({u, (int)g[u].size()-1, 0});
    }

    bool bfs(int s, int t) {
        fill(level.begin(), level.end(), -1);
        queue<int> q;
        level[s] = 0;
        q.push(s);
        while (!q.empty()) {
            int u = q.front();
            q.pop();
            for (auto &e : g[u]) {
                if (e.cap > 0 && level[e.to] == -1) {
                    level[e.to] = level[u] + 1;
                    if (e.to == t) return true;
                    q.push(e.to);
                }
            }
        }
        return false;
    }

    ll dfs(int u, int t, ll flow) {
        if (u == t) return flow;
        for (int &i = ptr[u]; i < g[u].size(); ++i) {
            Edge &e = g[u][i];
            if (e.cap > 0 && level[e.to] == level[u] + 1) {
                ll pushed = dfs(e.to, t, min(flow, e.cap));
                if (pushed > 0) {
                    e.cap -= pushed;
                    g[e.to][e.rev].cap += pushed;
                    return pushed;
                }
            }
        }
        return 0;
    }

    ll max_flow(int s, int t) {
        ll flow = 0;
        while (bfs(s, t)) {
            fill(ptr.begin(), ptr.end(), 0);
            while (ll pushed = dfs(s, t, INF)) {
                flow += pushed;
            }
        }
        return flow;
    }
};

struct MCFEdge {
    int from, to;
    ll cap, cost;
};

ll min_cost_flow(int s, int t, ll required_flow, const vector<MCFEdge> &edges, int n) {
    struct Edge {
        int to, rev;
        ll cap, cost;
    };

    vector<vector<Edge>> g(n+1);
    for (const auto &e : edges) {
        ll cap = e.cap;
        ll cost = e.cost;
        int a = e.from;
        int b = e.to;
        g[a].push_back({b, (int)g[b].size(), cap, cost});
        g[b].push_back({a, (int)g[a].size()-1, 0, -cost});
    }

    ll flow = 0;
    ll cost = 0;
    vector<ll> pot(n+1, 0);
    while (flow < required_flow) {
        vector<ll> dist(n+1, INF);
        vector<int> prev_node(n+1, -1);
        vector<int> prev_edge(n+1, -1);
        dist[s] = 0;
        priority_queue<pair<ll, int>, vector<pair<ll, int>>, greater<>> q;
        q.push({0, s});
        while (!q.empty()) {
            auto [d, u] = q.top();
            q.pop();
            if (d > dist[u]) continue;
            for (int i = 0; i < g[u].size(); ++i) {
                auto &e = g[u][i];
                if (e.cap > 0) {
                    ll new_dist = dist[u] + e.cost + pot[u] - pot[e.to];
                    if (new_dist < dist[e.to]) {
                        dist[e.to] = new_dist;
                        prev_node[e.to] = u;
                        prev_edge[e.to] = i;
                        q.push({new_dist, e.to});
                    }
                }
            }
        }
        if (dist[t] == INF) return INF;
        for (int u = 1; u <= n; ++u)
            if (dist[u] < INF) pot[u] += dist[u];
        ll augment = required_flow - flow;
        for (int v = t; v != s; v = prev_node[v])
            augment = min(augment, g[prev_node[v]][prev_edge[v]].cap);
        for (int v = t; v != s; v = prev_node[v]) {
            int u = prev_node[v];
            int e_idx = prev_edge[v];
            g[u][e_idx].cap -= augment;
            g[v][g[u][e_idx].rev].cap += augment;
            cost += g[u][e_idx].cost * augment;
        }
        flow += augment;
    }
    return cost;
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int n, k;
    cin >> n >> k;
    vector<vector<int>> c(n+1, vector<int>(n+1));
    for (int i = 1; i <= n; ++i)
        for (int j = 1; j <= n; ++j)
            cin >> c[i][j];

    Dinic dinic(n);
    for (int i = 1; i <= n; ++i)
        for (int j = 1; j <= n; ++j)
            if (c[i][j] > 0)
                dinic.add_edge(i, j, c[i][j]);

    ll F0 = dinic.max_flow(1, n);
    if (F0 == 0) {
        cout << "0\n";
        return 0;
    }

    vector<MCFEdge> edges;
    for (int i = 1; i <= n; ++i) {
        for (int j = 1; j <= n; ++j) {
            if (c[i][j] > 0) {
                edges.push_back({i, j, c[i][j], 0});
                edges.push_back({i, j, k + 1, 1});
            }
        }
    }

    ll left = F0, right = F0 + k, answer = F0;
    while (left <= right) {
        ll mid = (left + right) / 2;
        ll cost = min_cost_flow(1, n, mid, edges, n);
        if (cost <= k) {
            answer = mid;
            left = mid + 1;
        } else {
            right = mid - 1;
        }
    }

    cout << answer << endl;
    return 0;
}

