#include <bits/stdc++.h>
#include "beechtree.h"
#include <vector>
#include <queue>
#include <unordered_set>
#include <unordered_map>

using namespace std;

vector<int> beechtree(int N, int M, vector<int> P, vector<int> C) {
    vector<int> result(N, 0);
    for (int r = 0; r < N; ++r) {
        // Collect subtree T(r)
        vector<int> subtree;
        queue<int> q;
        q.push(r);
        subtree.push_back(r);
        while (!q.empty()) {
            int u = q.front();
            q.pop();
            for (int v = u + 1; v < N; ++v) {
                if (P[v] == u) {
                    subtree.push_back(v);
                    q.push(v);
                }
            }
        }

        // Check if all children of each node in the subtree have distinct colors
        bool valid = true;
        unordered_map<int, unordered_set<int>> children_colors;
        for (int u : subtree) {
            unordered_set<int> colors;
            for (int v : subtree) {
                if (P[v] == u) {
                    int c = C[v];
                    if (colors.count(c)) {
                        valid = false;
                        break;
                    }
                    colors.insert(c);
                }
            }
            if (!valid) break;
        }
        if (!valid) {
            result[r] = 0;
            continue;
        }

        // BFS to check if permutation is possible
        vector<int> perm;
        unordered_map<int, int> pos_map;
        unordered_map<int, int> color_count;
        queue<int> bfs_q;
        perm.push_back(r);
        pos_map[r] = 0;
        bfs_q.push(r);
        valid = true;

        while (!bfs_q.empty()) {
            int u = bfs_q.front();
            bfs_q.pop();

            // Collect children of u in the subtree
            vector<int> children;
            for (int v : subtree) {
                if (P[v] == u) {
                    children.push_back(v);
                }
            }

            for (int x : children) {
                int color = C[x];
                if (color_count[color] != pos_map[u]) {
                    valid = false;
                    break;
                }
                perm.push_back(x);
                pos_map[x] = perm.size() - 1;
                color_count[color]++;
                bfs_q.push(x);
            }
            if (!valid) break;
        }

        if (valid && perm.size() == subtree.size()) {
            result[r] = 1;
        } else {
            result[r] = 0;
        }
    }
    return result;
}

#include <bits/stdc++.h>
#include "choreography.h"
#include <vector>

using namespace std;

vector<int> inv;
int N;

void init(int n, vector<int> P) {
    N = n;
    inv.resize(N);
    for (int i = 0; i < N; ++i) {
        inv[P[i]] = i;
    }
}

void move_right(int K) {
    for (int d = 0; d < N; ++d) {
        inv[d] = (inv[d] + K) % N;
    }
}

void move_left(int K) {
    K = (N - K) % N;
    move_right(K);
}

void swap_places() {
    for (int d = 0; d < N; ++d) {
        inv[d] ^= 1;
    }
}

void move_around() {
    vector<int> pos(N);
    for (int d = 0; d < N; ++d) {
        pos[inv[d]] = d;
    }
    inv = pos;
}

int get_position(int D) {
    return inv[D];
}

