        vector<int> a(n), b(n), c(n);
        for (int& x : a) cin >> x;
        for (int& x : b) cin >> x;
        for (int& x : c) cin >> x;

        auto a_top = get_top3(a);
        auto b_top = get_top3(b);
        auto c_top = get_top3(c);

        long long max_sum = 0;

        for (const auto& a_pair : a_top) {
            int a_val = a_pair.first;
            int a_idx = a_pair.second;
            for (const auto& b_pair : b_top) {
                int b_val = b_pair.first;
                int b_idx = b_pair.second;
                if (a_idx == b_idx) continue;
                for (const auto& c_pair : c_top) {
                    int c_val = c_pair.first;
                    int c_idx = c_pair.second;
                    if (c_idx == a_idx || c_idx == b_idx) continue;
                    long long current_sum = (long long)a_val + b_val + c_val;
                    if (current_sum > max_sum) {
                        max_sum = current_sum;
                    }
                }
            }
        }

