#include <bits/stdc++.h>
using namespace std;

using uint64 = unsigned long long;

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    int n;
    cin >> n;

    uint64 a_xor = 0;
    vector<uint64> s_list;
    for (int i = 0; i < n; ++i) {
        uint64 a, b;
        cin >> a >> b;
        a_xor ^= a;
        s_list.push_back(a ^ b);
    }

    array<uint64, 64> basis{};
    for (uint64 s : s_list) {
        if (s == 0) continue;
        uint64 x = s;
        for (int j = 63; j >= 0; --j) {
            if ((x >> j) & 1) {
                if (basis[j] == 0) {
                    basis[j] = x;
                    break;
                }
                x ^= basis[j];
            }
        }
    }

    uint64 a = a_xor;
    for (int j = 63; j >= 0; --j) {
        if ((a >> j) & 1 && basis[j] != 0) {
            a ^= basis[j];
        }
    }
    bool in_span = (a == 0);

    int k = 0;
    for (int j = 0; j < 64; ++j) {
        if (basis[j] != 0) k++;
    }

    if (in_span) {
        uint64 numerator = (1ULL << k) - 1;
        uint64 denominator = 1ULL << k;
        uint64 g = gcd(numerator, denominator);
        cout << numerator/g << "/" << denominator/g << "\n";
    } else {
        cout << "1/1\n";
    }

    return 0;
}

