#include <bits/stdc++.h>
using namespace std;

int main() {
    int n;
    cin >> n;
    vector<int> p(n + 1);
    for (int i = 1; i <= n; ++i)
        cin >> p[i];

    vector<int> left_dec(n + 1), right_dec(n + 1);
    for (int i = 2; i <= n; ++i)
        left_dec[i] = (p[i - 1] < p[i]) ? left_dec[i - 1] + 1 : 0;
    for (int i = n - 1; i >= 1; --i)
        right_dec[i] = (p[i + 1] < p[i]) ? right_dec[i + 1] + 1 : 0;

    vector<int> left_inc(n + 1), right_inc(n + 1);
    for (int i = 2; i <= n; ++i)
        left_inc[i] = (p[i - 1] > p[i]) ? left_inc[i - 1] + 1 : 0;
    for (int i = n - 1; i >= 1; --i)
        right_inc[i] = (p[i + 1] > p[i]) ? right_inc[i + 1] + 1 : 0;

    vector<int> D_steps(n + 1);
    for (int i = 1; i <= n; ++i)
        D_steps[i] = max(left_inc[i], right_inc[i]);

    int M = *max_element(D_steps.begin(), D_steps.end());
    int cnt = 0;
    for (int i = 1; i <= n; ++i)
        cnt += (D_steps[i] == M);
    int S = 0;
    for (int i = 1; i <= n; ++i)
        if (D_steps[i] != M)
            S = max(S, D_steps[i]);

    vector<int> peaks;
    for (int i = 1; i <= n; ++i) {
        bool ok = false;
        if (i == 1)
            ok = (n > 1 && p[1] > p[2]);
        else if (i == n)
            ok = (n > 1 && p[n] > p[n - 1]);
        else
            ok = (p[i - 1] < p[i] && p[i] > p[i + 1]);
        if (ok)
            peaks.push_back(i);
    }

    int ans = 0;
    for (int x : peaks) {
        int min_arm = min(left_dec[x], right_dec[x]);
        int maxD;
        if (D_steps[x] == M) {
            maxD = (cnt > 1) ? M : S;
        } else {
            maxD = M;
        }
        if (min_arm > maxD)
            ans++;
    }

    cout << ans << endl;
}

#include <bits/stdc++.h>
#include "parks.h"
