#include <algorithm>

using namespace std;

int construct_roads(vector<int> x, vector<int> y) {
    int n = x.size();
    vector<pair<int, int>> sorted_pairs;
    for (int i = 0; i < n; ++i) {
        sorted_pairs.emplace_back(y[i], i);
    }
    sort(sorted_pairs.begin(), sorted_pairs.end());

    bool possible = true;
    for (int i = 0; i < n - 1; ++i) {
        if (sorted_pairs[i+1].first - sorted_pairs[i].first != 2) {
            possible = false;
            break;
        }
    }

    if (!possible) {
        return 0;
    }

    vector<int> u, v, a, b;
    for (int i = 0; i < n - 1; ++i) {
        u.push_back(sorted_pairs[i].second);
        v.push_back(sorted_pairs[i+1].second);
        a.push_back(1); // All benches placed at x=1 (left side)
        b.push_back(sorted_pairs[i].first + 1);
    }

    build(u, v, a, b);
    return 1;
}

