const int MAX = 2e5 + 5;
vector<bool> prime;

void sieve() {
    prime.resize(MAX, true);
    prime[0] = prime[1] = false;
    for (int i = 2; i * i < MAX; ++i) {
        if (prime[i]) {
            for (int j = i * i; j < MAX; j += i) {
                prime[j] = false;
            }
        }
    }
}

bool is_splittable(int x) {
    if (x < 2) return false;
    if (x % 2 == 0) {
        return x >= 4;
    } else {
        return x >= 5 && prime[x - 2];
    }
}

