using ll = long long;
const int MOD = 1e9 + 7;
const int MAX = 2e5 + 10;

vector<ll> fact(MAX), inv_fact(MAX);

ll powmod(ll a, ll b) {
    ll res = 1;
    for (; b; b >>= 1) {
        if (b & 1) res = res * a % MOD;
        a = a * a % MOD;
    }
    return res;
}

void precompute() {
    fact[0] = 1;
    for (int i = 1; i < MAX; ++i) {
        fact[i] = fact[i-1] * i % MOD;
    }
    inv_fact[MAX-1] = powmod(fact[MAX-1], MOD-2);
    for (int i = MAX-2; i >= 0; --i) {
        inv_fact[i] = inv_fact[i+1] * (i+1) % MOD;
    }
}

ll comb(ll n, ll k) {
    if (n < 0 || k < 0 || n < k) return 0;
    return fact[n] * inv_fact[k] % MOD * inv_fact[n - k] % MOD;
}

bool less_eq(const string &a, const string &b) {
    if (a.size() != b.size()) return a.size() <= b.size();
    return a <= b;
}

string generate_minimal(int m1, int m0, int c11, int c00) {
    vector<int> runs1(m1, 1);
    if (m1 > 0) runs1.back() += c11;
    vector<int> runs0(m0, 1);
    if (m0 > 0) runs0.back() += c00;
    string s;
    bool one = true;
    int i1 = 0, i0 = 0;
    while (i1 < m1 || i0 < m0) {
        if (one && i1 < m1) {
            s += string(runs1[i1], '1');
            i1++;
            one = false;
        } else if (!one && i0 < m0) {
            s += string(runs0[i0], '0');
            i0++;
            one = true;
        } else {
            break;
        }
    }
    return s;
}

string generate_maximal(int m1, int m0, int c11, int c00) {
    vector<int> runs1(m1, 1);
    if (m1 > 0) runs1.front() += c11;
    vector<int> runs0(m0, 1);
    if (m0 > 0) runs0.front() += c00;
    string s;
    bool one = true;
    int i1 = 0, i0 = 0;
    while (i1 < m1 || i0 < m0) {
        if (one && i1 < m1) {
            s += string(runs1[i1], '1');
            i1++;
            one = false;
        } else if (!one && i0 < m0) {
            s += string(runs0[i0], '0');
            i0++;
            one = true;
        } else {
            break;
        }
    }
    return s;
}

