                                    ans = (ans + ways) % MOD;
                                }
                            }
                        }
                    }
                }
            }
        }
    }

    if (c10 == c01 + 1) {
        int T = c01 + c10;
        if (T % 2 != 1) {
            // invalid
        } else {
            int m1 = (T + 1) / 2;
            int m0 = m1;
            int sum_run1 = c11 + m1;
            int sum_run0 = c00 + m0;
            if (sum_run1 < m1 || sum_run0 < m0) {
