            } else {
                int L = sum_run1 + sum_run0;
                ll ways = comb(c00 + m0 -1, m0-1) * comb(c11 + m1 -1, m1-1) % MOD;
                if (ways == 0) {
                    // do nothing
                } else {
                    string min_s = generate_minimal(m1, m0, c11, c00);
                    string max_s = generate_maximal(m1, m0, c11, c00);
                    if (L < (int)A.size() || L > (int)B.size()) {
                        // do nothing
                    } else if (L > (int)A.size() && L < (int)B.size()) {
                        ans = (ans + ways) % MOD;
                    } else {
                        bool ok_min = less_eq(A, max_s) && less_eq(min_s, B);
                        if (ok_min) {
                            if (L == (int)A.size() && L == (int)B.size()) {
                                if (less_eq(A, min_s) && less_eq(max_s, B)) {
                                    ans = (ans + ways) % MOD;
                                } else {
                                    // check if there's overlap
                                    if (less_eq(A, max_s) && less_eq(min_s, B)) {
                                        ans = (ans + ways) % MOD;
