#include <bits/stdc++.h>
#include "closing.h"
#include <vector>
#include <algorithm>
#include <queue>
#include <stack>
#include <tuple>
#include <functional>
using namespace std;

using ll = long long;

struct Edge {
    int to, w;
    Edge(int to, int w) : to(to), w(w) {}
};

vector<vector<Edge>> adj;
vector<int> parent;
vector<ll> dx, dy;
vector<int> x_path;

bool find_path(int X, int Y, int N) {
    parent.assign(N, -1);
    queue<int> q;
    q.push(X);
    parent[X] = X;
    while (!q.empty()) {
        int u = q.front();
        q.pop();
        if (u == Y) break;
        for (const Edge& e : adj[u]) {
            if (parent[e.to] == -1) {
                parent[e.to] = u;
                q.push(e.to);
            }
        }
    }
    if (parent[Y] == -1) return false;
    x_path.clear();
    for (int v = Y; ; v = parent[v]) {
        x_path.push_back(v);
        if (v == X) break;
    }
    reverse(x_path.begin(), x_path.end());
    return true;
}

void compute_dx_dy(int X, int Y, int N) {
    dx.assign(N, 0);
    dy.assign(N, 0);
    vector<bool> visited(N, false);
    stack<int> s;
    s.push(X);
    visited[X] = true;
    while (!s.empty()) {
        int u = s.top();
        s.pop();
        for (const Edge& e : adj[u]) {
            if (!visited[e.to] && e.to != parent[u]) {
                parent[e.to] = u;
                dx[e.to] = dx[u] + e.w;
                visited[e.to] = true;
                s.push(e.to);
            }
        }
    }
    visited.assign(N, false);
    s.push(Y);
    visited[Y] = true;
    parent[Y] = Y;
    while (!s.empty()) {
        int u = s.top();
        s.pop();
        for (const Edge& e : adj[u]) {
            if (!visited[e.to] && e.to != parent[u]) {
                parent[e.to] = u;
                dy[e.to] = dy[u] + e.w;
                visited[e.to] = true;
                s.push(e.to);
            }
        }
    }
}

vector<ll> sum_x, sum_y, sum_min;
vector<ll> sum_subtree_x, sum_subtree_y;
vector<int> size_subtree_x, size_subtree_y;

void dfs_subtree(int u, int p, const vector<ll>& d, vector<ll>& sum_dist, vector<int>& size, vector<ll>& sum_subtree, vector<int>& size_sub) {
    sum_dist[u] = 0;
    size[u] = 1;
    for (const Edge& e : adj[u]) {
        if (e.to != p && find(x_path.begin(), x_path.end(), e.to) == x_path.end()) {
            dfs_subtree(e.to, u, d, sum_dist, size, sum_subtree, size_sub);
            sum_dist[u] += sum_dist[e.to] + e.w * size[e.to];
            size[u] += size[e.to];
        }
    }
    sum_subtree[u] = d[u] * size[u] + sum_dist[u];
    size_sub[u] = size[u];
}

void precompute_subtrees(const vector<int>& path) {
    int m = path.size();
    sum_subtree_x.assign(m, 0);
    sum_subtree_y.assign(m, 0);
    size_subtree_x.assign(m, 0);
    size_subtree_y.assign(m, 0);
    vector<ll> sum_dist_x(adj.size(), 0), sum_dist_y(adj.size(), 0);
    vector<int> size_x(adj.size(), 0), size_y(adj.size(), 0);
    for (int i = 0; i < m; ++i) {
        int u = path[i];
        dfs_subtree(u, parent[u], dx, sum_dist_x, size_x, sum_subtree_x, size_subtree_x);
        dfs_subtree(u, parent[u], dy, sum_dist_y, size_y, sum_subtree_y, size_subtree_y);
    }
}

int max_score(int N, int X, int Y, long long K,
              vector<int> U, vector<int> V, vector<int> W) {
    adj.assign(N, vector<Edge>());
    for (int i = 0; i < N-1; ++i) {
        adj[U[i]].emplace_back(V[i], W[i]);
        adj[V[i]].emplace_back(U[i], W[i]);
    }
    if (!find_path(X, Y, N)) return 0;
    compute_dx_dy(X, Y, N);
    vector<int>& path = x_path;
    int m = path.size();
    ll D = dx[Y];
    sum_x.assign(m, 0);
    sum_y.assign(m, 0);
    sum_min.assign(m, 0);
    for (int i = 0; i < m; ++i) {
        int u = path[i];
        if (i == 0) sum_x[i] = dx[u];
        else sum_x[i] = sum_x[i-1] + dx[u];
        sum_min[i] = (i > 0 ? sum_min[i-1] : 0) + min(dx[u], D - dx[u]);
    }
    for (int i = m-1; i >= 0; --i) {
        int u = path[i];
        if (i == m-1) sum_y[i] = dy[u];
        else sum_y[i] = sum_y[i+1] + dy[u];
    }
    precompute_subtrees(path);
    ll best = 0;
    int j = m-1;
    for (int i = 0; i < m; ++i) {
        ll cost_x = sum_x[i] + sum_subtree_x[i];
        while (j >= 0) {
            ll cost_y = sum_y[j] + sum_subtree_y[j];
            ll overlap = 0;
            if (i >= j) {
                overlap = sum_min[i] - (j > 0 ? sum_min[j-1] : 0);
            } else {
                overlap = sum_min[j] - (i > 0 ? sum_min[i-1] : 0);
            }
            ll total = cost_x + cost_y - overlap;
            if (total <= K) {
                ll score = (i+1 + size_subtree_x[i]) + ((m - j) + size_subtree_y[j]);
                best = max(best, score);
                break;
            }
            --j;
        }
    }
    return best;
}

