#include <bits/stdc++.h>
using namespace std;

struct Point {
    long long x, y;
    Point operator-(const Point& p) const { return {x - p.x, y - p.y}; }
    long long cross(const Point& p) const { return x * p.y - y * p.x; }
};

vector<Point> pts;
int n;
long long totalArea;

vector<vector<long long>> memo;

long long computeArea(int i, int j, int k) {
    return abs((pts[j] - pts[i]).cross(pts[k] - pts[i]));
}

long long dp(int L, int R, int turn) {
    if (L + 1 >= R) return 0;
    if (memo[L][R] != -1) return memo[L][R];
    long long res = turn == 0 ? LLONG_MAX : 0;
    for (int k = L + 1; k < R; ++k) {
        long long current_area = computeArea(L, k, R);
        if (turn == 0) { // Alberto's turn: choose to minimize his sum
            long long next_sum = dp(L, k, 1) + dp(k, R, 1);
            res = min(res, current_area + next_sum);
        } else { // Beatrice's turn: choose to maximize Alberto's sum (minimize her own)
            long long next_sum = dp(L, k, 0) + dp(k, R, 0);
            res = max(res, next_sum);
        }
    }
    return memo[L][R] = res;
}

int main() {
    cin >> n;
    pts.resize(n);
    for (int i = 0; i < n; ++i) {
        cin >> pts[i].x >> pts[i].y;
    }

    // Compute total area of the polygon
    totalArea = 0;
    for (int i = 0; i < n; ++i) {
        int j = (i + 1) % n;
        totalArea += pts[i].x * pts[j].y - pts[i].y * pts[j].x;
    }
    totalArea = abs(totalArea);

    memo.assign(n, vector<long long>(n, -1));
    long long alberto = dp(0, n-1, 0);
    long long beatrice = totalArea - alberto;

    if (alberto * 2 <= totalArea) {
        cout << "Alberto\n";
    } else if (beatrice * 2 <= totalArea) {
        cout << "Beatrice\n";
    } else {
        cout << "-\n";
    }

    return 0;
}

