void solve() {
    cin >> n >> k >> q;
    for (int i = 1; i <= n; i++) cin >> a[i];
    for (int i = 1; i <= n; i++) cin >> b[i];
    for (int i = 1; i <= n; i++) d[i] = a[i] ^ b[i];
    fill(f, f + B, 1e9);
    f[0] = 0;
    cnt = B - 1;
    for (int i = 1; i <= n - k + 1; i++) {
        int x = d[i] ^ d[i + k];
        upd(x, 1);
        d[i + k] ^= x;
    }
    for (int i = n - k + 2; i <= n; i++) if (d[i]) { cnt = -1; break; }
    cout << cnt << "\n";
    while (q--) {
        char s; int p, v; cin >> s >> p >> v;
        int& t = (s == 'a' ? a[p] : b[p]);
        int diff = t ^ v;
        t = v;
        int l = max(1LL, p - k + 1);
        int r = min(n - k + 1, p);
        fill(f, f + B, 1e9);
        f[0] = 0;
        cnt = B - 1;
        for (int i = l; i <= r; i++) {
            int x = d[i] ^ d[i + k];
            upd(x, 1);
            d[i + k] ^= x;
        }
        for (int i = n - k + 2; i <= n; i++) if (d[i]) { cnt = -1; break; }
        cout << cnt << "\n";
    }
}

