vector<int> malfunctioning_cores(int N) {
    vector<int> result(N, 0);
    vector<int> zeros;

    // Check for s=2 case
    for (int i = 0; i < N; ++i) {
        int res = run_diagnostic({i});
        if (res == 0) {
            zeros.push_back(i);
        }
    }

    if (zeros.size() == 2) {
        result[zeros[0]] = 1;
        result[zeros[1]] = 1;
        return result;
    }

    // Binary search to find a subset T that returns 0
    vector<int> allCores;
    for (int i = 0; i < N; ++i) {
        allCores.push_back(i);
    }

    vector<int> T;
    bool found = false;

    // Helper function to check subsets
    auto findT = [&](vector<int> candidates) {
        if (candidates.empty()) return;
        int res = run_diagnostic(candidates);
        if (res == 0) {
            T = candidates;
            found = true;
            return;
        }
        if (candidates.size() == 1) return;

        vector<int> left(candidates.begin(), candidates.begin() + candidates.size()/2);
        findT(left);
        if (found) return;
        vector<int> right(candidates.begin() + candidates.size()/2, candidates.end());
        findT(right);
        if (found) return;
    };

    findT(allCores);

    if (!found) {
        // Fallback: try subsets of different sizes (not optimal)
        for (int len = 1; len <= N && !found; ++len) {
            for (int mask = 1; mask < (1 << N); ++mask) {
                if (__builtin_popcount(mask) != len) continue;
                vector<int> subset;
                for (int i = 0; i < N; ++i) {
                    if (mask & (1 << i)) subset.push_back(i);
                }
                int res = run_diagnostic(subset);
                if (res == 0) {
                    T = subset;
                    found = true;
                    break;
                }
            }
        }
    }

    // Determine each core's status using T
    for (int i = 0; i < N; ++i) {
        auto it = find(T.begin(), T.end(), i);
        if (it != T.end()) {
            vector<int> newT = T;
            newT.erase(remove(newT.begin(), newT.end(), i), newT.end());
            sort(newT.begin(), newT.end());
            int res = run_diagnostic(newT);
            if (res == -1) {
                result[i] = 1;
            }
        } else {
            vector<int> newT = T;
            newT.push_back(i);
            sort(newT.begin(), newT.end());
            int res = run_diagnostic(newT);
            if (res == 1) {
                result[i] = 1;
            }
        }
    }

    return result;
}

