using namespace std;

struct Option {
    long long cost;
    int gain;
    Option(long long c, int g) : cost(c), gain(g) {}
    bool operator<(const Option& other) const {
        return cost < other.cost;
    }
};

vector<Option> build_list(const vector<long long>& d, int start, int end, int step, int gain_step) {
    vector<Option> list;
    for (int i = start; i != end; i += step) {
        long long cost = d[i];
        int gain = (gain_step > 0) ? (i - start) / gain_step + 1 : (start - i) / (-gain_step) + 1;
        list.emplace_back(cost, gain);
    }
    sort(list.begin(), list.end());
    return list;
}

int compute_max_gain(const vector<Option>& list, long long budget) {
    int left = 0, right = list.size();
    int best_gain = 0;
    while (left < right) {
        int mid = (left + right) / 2;
        if (list[mid].cost <= budget) {
            best_gain = list[mid].gain;
            left = mid + 1;
        } else {
            right = mid;
        }
    }
    return best_gain;
}

int max_score(int N, int X, int Y, long long K,
              vector<int> U, vector<int> V, vector<int> W) {
    vector<long long> dX(N, 0);
    vector<long long> dY(N, 0);

    for (int i = X + 1; i < N; ++i) {
        dX[i] = dX[i-1] + W[i-1];
    }
    for (int i = X - 1; i >= 0; --i) {
        dX[i] = dX[i+1] + W[i];
    }

    for (int i = Y + 1; i < N; ++i) {
        dY[i] = dY[i-1] + W[i-1];
    }
    for (int i = Y - 1; i >= 0; --i) {
        dY[i] = dY[i+1] + W[i];
    }

    vector<Option> L_list;
    for (int i = X - 1; i >= 0; --i) {
        L_list.emplace_back(dX[i], X - i);
    }
    sort(L_list.begin(), L_list.end());

    vector<Option> R_list;
    for (int i = Y + 1; i < N; ++i) {
        R_list.emplace_back(dY[i], i - Y);
    }
    sort(R_list.begin(), R_list.end());

    int middle_len = Y - X + 1;
    vector<long long> sum_S(middle_len, 0);
    vector<long long> sum_T(middle_len, 0);

    for (int s = X + 1; s <= Y; ++s) {
        sum_S[s - X] = sum_S[s - X - 1] + dX[s];
    }

    for (int s = Y - 1; s >= X; --s) {
        sum_T[s - X] = sum_T[s - X + 1] + dY[s];
    }

    long long min_cost_middle = numeric_limits<long long>::max();
    for (int s = X; s <= Y; ++s) {
        int idx = s - X;
        long long current_sum_S = sum_S[idx];
        long long current_sum_T = sum_T[idx];
        long long current_min = min(dX[s], dY[s]);
        long long current_cost = current_sum_S + current_sum_T - current_min;
        if (current_cost < min_cost_middle) {
            min_cost_middle = current_cost;
        }
    }

    int scenario_overlap = 0;
    if (min_cost_middle <= K) {
        long long rem = K - min_cost_middle;
        int max_gain_LR = 0;
        int i = 0, j = R_list.size() - 1;
        while (i < L_list.size() && j >= 0) {
            long long current_cost = L_list[i].cost + R_list[j].cost;
            if (current_cost <= rem) {
                max_gain_LR = max(max_gain_LR, L_list[i].gain + R_list[j].gain);
                i++;
            } else {
                j--;
            }
        }
        scenario_overlap = (Y - X + 2) + max_gain_LR;
    }

    int scenario_non_overlap = 0;
    vector<long long> sum_T_middle(middle_len);
    for (int s = X; s <= Y; ++s) {
        sum_T_middle[s - X] = sum_T[s - X];
    }

    for (int a = X; a <= Y; ++a) {
        int a_idx = a - X;
        long long current_sum_S = sum_S[a_idx];
        if (current_sum_S > K) continue;
        long long available = K - current_sum_S;
        int left = a + 1;
        int right = Y;
        int best_b = -1;
        while (left <= right) {
            int mid = (left + right) / 2;
            int mid_idx = mid - X;
            if (mid_idx >= middle_len) break;
            long long sum_t = sum_T_middle[mid_idx];
            if (sum_t <= available) {
                best_b = mid;
                left = mid + 1;
            } else {
                right = mid - 1;
            }
        }
        int gain = (a - X + 1);
        if (best_b != -1 && best_b >= a + 1) {
            gain += (Y - best_b + 1);
        }
        scenario_non_overlap = max(scenario_non_overlap, gain);
    }

    int scenario_LR = 0;
    int i_LR = 0, j_LR = R_list.size() - 1;
    while (i_LR < L_list.size() && j_LR >= 0) {
        long long current_cost = L_list[i_LR].cost + R_list[j_LR].cost;
        if (current_cost <= K) {
            scenario_LR = max(scenario_LR, L_list[i_LR].gain + R_list[j_LR].gain);
            i_LR++;
        } else {
            j_LR--;
        }
    }

    int max_L = compute_max_gain(L_list, K);
    int max_R = compute_max_gain(R_list, K);

    return max({scenario_overlap, scenario_non_overlap, scenario_LR, max_L, max_R});
}

#include <bits/stdc++.h>
using namespace std;

bool check(int m, const vector<pair<int, int>>& friends) {
    int current_j = 0;
    for (const auto& [a, b] : friends) {
        if (current_j >= m) break;
        int L = max(m - a - 1, 0);
        int R = min(b, m-1);
        if (current_j >= L && current_j <= R) {
            current_j++;
        }
    }
    return current_j >= m;
}

