        for (int i = 0; i < n; ++i) {
            int a, b;
            cin >> a >> b;
            friends[i] = {a, b};
        }
        int low = 0, high = n, ans = 0;
        while (low <= high) {
            int mid = (low + high) / 2;
            if (check(mid, friends)) {
                ans = mid;
                low = mid + 1;
            } else {
                high = mid - 1;
            }
        }
        cout << ans << '\n';
    }
    return 0;
}

