#include <iostream>
#include <vector>
using namespace std;

vector<bool> sieve;

void generate_sieve(int max_n) {
    sieve.assign(max_n + 1, true);
    sieve[0] = sieve[1] = false;
    for (int i = 2; i * i <= max_n; ++i) {
        if (sieve[i]) {
            for (int j = i * i; j <= max_n; j += i) {
                sieve[j] = false;
            }
        }
    }
}

int reverse_number(int p) {
    int reversed = 0;
    while (p > 0) {
        reversed = reversed * 10 + (p % 10);
        p /= 10;
    }
    return reversed;
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    const int MAX = 10000000;
    generate_sieve(MAX);

    vector<int> primes;
    primes.reserve(664579); // Approximate number of primes under 1e7
    for (int i = 2; i <= MAX; ++i) {
        if (sieve[i]) {
            primes.push_back(i);
        }
    }

    vector<int> emirps;
    emirps.reserve(12000);
    for (int p : primes) {
        if (p == 2 || p == 5) continue;
        int q = reverse_number(p);
        if (q > MAX) continue; // Reverse is beyond sieve's limit
        if (sieve[q] && q != p) {
            emirps.push_back(p);
        }
        if (emirps.size() >= 11184) break; // Early exit if enough emirps are found
    }

    int d;
    cin >> d;
    cout << emirps[d - 1] << '\n';

    return 0;
}

