#include <bits/stdc++.h>
using namespace std;

using pii = pair<int, int>;

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int n, m;
    cin >> n >> m;
    vector<vector<int>> grid(n, vector<int>(m));
    vector<pii> pos(n * m + 1);

    for (int i = 0; i < n; ++i) {
        for (int j = 0; j < m; ++j) {
            cin >> grid[i][j];
            pos[grid[i][j]] = {i, j};
        }
    }

    vector<int> bad;
    for (int i = 1; i <= n * m - 1; ++i) {
        pii p = pos[i + 1];
        bool ok = false;
        int dx[] = {-1, 1, 0, 0};
        int dy[] = {0, 0, -1, 1};
        for (int d = 0; d < 4; ++d) {
            int nx = p.first + dx[d];
            int ny = p.second + dy[d];
            if (nx >= 0 && nx < n && ny >= 0 && ny < m) {
                if (grid[nx][ny] <= i) {
                    ok = true;
                    break;
                }
            }
        }
        if (!ok) {
            bad.push_back(i);
        }
    }

    if (bad.empty()) {
        cout << "0\n";
        return 0;
    }

    if (bad.size() > 2) {
        cout << "2\n";
        return 0;
    }

    set<pair<pii, pii>> valid_swaps;

    for (int i : bad) {
        pii p = pos[i + 1];
        int dx[] = {-1, 1, 0, 0};
        int dy[] = {0, 0, -1, 1};
        for (int d = 0; d < 4; ++d) {
            int nx = p.first + dx[d];
            int ny = p.second + dy[d];
            if (nx < 0 || nx >= n || ny < 0 || ny >= m) continue;
            int val = grid[nx][ny];
            if (val > i) {
                pii adj_pos = {nx, ny};
                for (int v = 1; v <= i; ++v) {
                    pii v_pos = pos[v];
                    if (v_pos == adj_pos) continue;
                    // Check swap adj_pos and v_pos
                    pii a = adj_pos;
                    pii b = v_pos;
                    if (a > b) swap(a, b);
                    valid_swaps.insert({a, b});
                }
            }
        }

        // Check swaps involving i+1's cell
        pii current = pos[i + 1];
        for (int v = 1; v <= n * m; ++v) {
            if (v == i + 1) continue;
            pii other = pos[v];
            // Check swap current and other
            pii a = current;
            pii b = other;
            if (a > b) swap(a, b);
            valid_swaps.insert({a, b});
        }
    }

    int count = 0;
    for (auto swap_pair : valid_swaps) {
        pii a = swap_pair.first;
        pii b = swap_pair.second;
        int val_a = grid[a.first][a.second];
        int val_b = grid[b.first][b.second];

        // Perform the swap
        swap(grid[a.first][a.second], grid[b.first][b.second]);
        bool ok = true;
        for (int i : bad) {
            pii p = pos[i + 1];
            // If i+1 was in a or b, update its position
            if (p == a) {
                p = b;
            } else if (p == b) {
                p = a;
            }
            bool found = false;
            int dx[] = {-1, 1, 0, 0};
            int dy[] = {0, 0, -1, 1};
            for (int d = 0; d < 4; ++d) {
                int nx = p.first + dx[d];
                int ny = p.second + dy[d];
                if (nx >= 0 && nx < n && ny >= 0 && ny < m) {
                    if (grid[nx][ny] <= i) {
                        found = true;
                        break;
                    }
                }
            }
            if (!found) {
                ok = false;
                break;
            }
        }
        // Revert the swap
        swap(grid[a.first][a.second], grid[b.first][b.second]);

        if (ok) {
            count++;
        }
    }

    if (count > 0) {
        cout << "1 " << count << "\n";
    } else {
        cout << "2\n";
    }

    return 0;
}

#include <bits/stdc++.h>
using namespace std;

struct Move {
    int s, t, j, vc, vm;
    Move(int s, int t, int j, int vc, int vm) : s(s), t(t), j(j), vc(vc), vm(vm) {}
};

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int N, M;
    cin >> N >> M;

    vector<int> sc(N), sm(N);
    for (int i = 0; i < N; ++i) {
        cin >> sc[i] >> sm[i];
    }

    vector<int> vc(M), vm(M);
    for (int i = 0; i < M; ++i) {
        cin >> vc[i] >> vm[i];
    }

    vector<int> old_j(M), new_j(M);
    for (int i = 0; i < M; ++i) {
        cin >> old_j[i] >> new_j[i];
    }

    vector<Move> moves;
    vector<int> initial_cpu(N, 0), initial_mem(N, 0);
    for (int i = 0; i < M; ++i) {
        if (old_j[i] != new_j[i]) {
            moves.emplace_back(old_j[i], new_j[i], i, vc[i], vm[i]);
            initial_cpu[old_j[i]] += vc[i];
            initial_mem[old_j[i]] += vm[i];
        }
    }

    sort(moves.begin(), moves.end(), [](const Move& a, const Move& b) {
        return (a.vc + a.vm) > (b.vc + b.vm);
    });

    vector<int> current_cpu = initial_cpu;
    vector<int> current_mem = initial_mem;

    vector<vector<Move>> steps;
    vector<bool> scheduled(moves.size(), false);

    while (true) {
        vector<Move> step;
        vector<int> move_count_s(N, 0), move_count_t(N, 0);
        vector<int> sum_in_cpu(N, 0), sum_in_mem(N, 0);
        vector<int> sum_out_cpu(N, 0), sum_out_mem(N, 0);
        bool added = false;

        for (size_t i = 0; i < moves.size(); ++i) {
            if (scheduled[i]) continue;
            const Move& m = moves[i];
            int s = m.s;
            int t = m.t;
            if (move_count_s[s] >= 2 || move_count_t[t] >= 2) continue;
            if (sum_in_cpu[t] + m.vc > sc[t] - current_cpu[t]) continue;
            if (sum_in_mem[t] + m.vm > sm[t] - current_mem[t]) continue;

            scheduled[i] = true;
            step.push_back(m);
            move_count_s[s]++;
            move_count_t[t]++;
            sum_in_cpu[t] += m.vc;
            sum_in_mem[t] += m.vm;
            sum_out_cpu[s] += m.vc;
            sum_out_mem[s] += m.vm;
            added = true;
        }

        if (!added) break;

        for (int t = 0; t < N; ++t) {
            current_cpu[t] += sum_in_cpu[t];
            current_mem[t] += sum_in_mem[t];
        }
        for (int s = 0; s < N; ++s) {
            current_cpu[s] -= sum_out_cpu[s];
            current_mem[s] -= sum_out_mem[s];
        }

        steps.push_back(step);
    }

    cout << steps.size() << '\n';
    for (const auto& step : steps) {
        cout << step.size() << '\n';
        for (const auto& m : step) {
            cout << m.s << ' ' << m.t << ' ' << m.j << '\n';
        }
    }

    return 0;
}

#include <bits/stdc++.h>
using namespace std;

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int n;
    cin >> n;
    int total = (1 << n) - 1;
    vector<int> v(total);
    for (int i = 0; i < total; ++i)
        cin >> v[i];

    vector<int> allowed(n, 0); // 0: none, 1: 0, 2: 1, 3: both
    bool possible = true;

    for (int i = 0; i < n; ++i) {
        int mask = 1 << i;
        int idx = mask - 1;
        int val = v[idx];
        int a0 = (val & (1 << 0)) ? 1 : 0;
        int a1 = (val & (1 << 1)) ? 1 : 0;
        if (!a0 && !a1) {
            possible = false;
            break;
        }
        allowed[i] = (a0 ? 1 : 0) | (a1 ? 2 : 0);
    }

    if (!possible) {
        cout << "0\n";
        return 0;
    }

    vector<int> masks;
    vector<int> bits;
    for (int i = 0; i < n; ++i) {
        int a = allowed[i];
        if (a == 3) {
            bits.push_back(i);
        } else {
            masks.push_back( (a & 1) ? 0 : (1 << i) );
        }
    }

    vector<int> candidates;
    int m = bits.size();
    for (int mask = 0; mask < (1 << m); ++mask) {
        int s = 0;
        for (int i = 0; i < m; ++i) {
            if (mask & (1 << i))
                s |= 1 << bits[i];
        }
        for (int fixed : masks)
            s |= fixed;
        candidates.push_back(s);
    }

    vector<pair<int, int>> non_singletons;
    for (int T = 1; T < (1 << n); ++T) {
        if (__builtin_popcount(T) == 1)
            continue;
        non_singletons.emplace_back(T, v[T-1]);
    }

    vector<int> valid;
    for (int s : candidates) {
        bool ok = true;
        for (auto& [T, val] : non_singletons) {
            int cnt = __builtin_popcount(s & T);
            if (!(val & (1 << cnt))) {
                ok = false;
                break;
            }
        }
        if (ok)
            valid.push_back(s);
    }

    sort(valid.begin(), valid.end());
    cout << valid.size() << '\n';
    for (int s : valid)
        cout << s << '\n';

    return 0;
}

#include <bits/stdc++.h>
using namespace std;

const int dx[] = {-1, 0, 1, 0};
const int dy[] = {0, 1, 0, -1};

void solve() {
    int n, m;
    cin >> n >> m;
    vector<string> grid(n);
    int lab_i = -1, lab_j = -1;
    for (int i = 0; i < n; ++i) {
        cin >> grid[i];
        for (int j = 0; j < m; ++j) {
            if (grid[i][j] == 'L') {
                lab_i = i;
                lab_j = j;
            }
        }
    }

    vector<vector<vector<int>>> available_dirs(n, vector<vector<int>>(m));
    for (int i = 0; i < n; ++i) {
        for (int j = 0; j < m; ++j) {
            if (grid[i][j] == '#') continue;
            for (int dir = 0; dir < 4; ++dir) {
                int ni = i + dx[dir];
                int nj = j + dy[dir];
                if (ni >= 0 && ni < n && nj >= 0 && nj < m && grid[ni][nj] != '#') {
                    available_dirs[i][j].push_back(dir);
                }
            }
        }
    }

    vector<vector<array<int, 4>>> count(n, vector<array<int, 4>>(m));
    vector<vector<vector<tuple<int, int, int>>>> dependents(n, vector<vector<tuple<int, int, int>>>(m));
    for (int i = 0; i < n; ++i) {
        for (int j = 0; j < m; ++j) {
            if (grid[i][j] == '#') continue;
            for (int D = 0; D < 4; ++D) {
                vector<int> required_dirs;
                for (int dir : available_dirs[i][j]) {
                    if (dir != D) {
                        required_dirs.push_back(dir);
                    }
                }
                count[i][j][D] = required_dirs.size();
                for (int dir : required_dirs) {
                    int ni = i + dx[dir];
                    int nj = j + dy[dir];
                    dependents[ni][nj].emplace_back(i, j, D);
                }
            }
        }
    }

    vector<vector<bool>> in_sol(n, vector<bool>(m, false));
    queue<pair<int, int>> q;
    in_sol[lab_i][lab_j] = true;
    q.emplace(lab_i, lab_j);

    while (!q.empty()) {
        auto [i, j] = q.front();
        q.pop();
        for (auto [x, y, D] : dependents[i][j]) {
            count[x][y][D]--;
            if (count[x][y][D] == 0 && !in_sol[x][y] && grid[x][y] == '.') {
                in_sol[x][y] = true;
                q.emplace(x, y);
            }
        }
    }

    for (int i = 0; i < n; ++i) {
        for (int j = 0; j < m; ++j) {
            if (grid[i][j] == 'L') {
                cout << 'L';
            } else if (grid[i][j] == '#') {
                cout << '#';
            } else {
                cout << (in_sol[i][j] ? '+' : '.');
            }
        }
        cout << '\n';
    }
}

