}

#include <bits/stdc++.h>
using namespace std;

const int MAX = 1e5 + 10;
vector<int> spf;
vector<vector<int>> divisors(MAX);

void sieve() {
    spf.resize(MAX);
    iota(spf.begin(), spf.end(), 0);
    for (int i = 2; i * i < MAX; ++i) {
        if (spf[i] == i) {
            for (int j = i * i; j < MAX; j += i) {
                if (spf[j] == j) spf[j] = i;
            }
        }
    }
}

vector<int> factorize(int x) {
    vector<int> res;
    if (x <= 1) return res;
    while (x > 1) {
        int p = spf[x];
        while (x % p == 0) {
            res.push_back(p);
            x /= p;
        }
    }
    return res;
}

vector<int> get_divisors(int x) {
    vector<int> factors = factorize(x);
    map<int, int> prime_counts;
    for (int p : factors) prime_counts[p]++;
    vector<int> divs = {1};
    for (auto &[p, cnt] : prime_counts) {
        int current_size = divs.size();
        for (int i = 0; i < current_size; ++i) {
            int d = divs[i];
            int p_power = 1;
            for (int j = 0; j < cnt; ++j) {
                p_power *= p;
                divs.push_back(d * p_power);
            }
        }
    }
    sort(divs.begin(), divs.end());
    divs.erase(unique(divs.begin(), divs.end()), divs.end());
    return divs;
}

void precompute_divisors() {
    for (int i = 1; i < MAX; ++i) {
        divisors[i] = get_divisors(i);
    }
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);
    sieve();
    precompute_divisors();

    int t;
    cin >> t;
    while (t--) {
        int n;
        cin >> n;
        vector<int> p(n);
        for (int &num : p) cin >> num;

        unordered_map<int, unordered_map<int, int>> data;
        long long ans = 0;

        for (int i = 1; i <= n; ++i) {
            int pi = p[i - 1];
            int g = gcd(pi, i);
            int a = pi / g;
            int b = i / g;

            vector<int> factors = factorize(b);
            unordered_set<int> primes_b(factors.begin(), factors.end());
            int q = 1;
            for (int p : primes_b) q *= p;

            int m = q;
            int current = 0;
            if (m <= MAX) {
                for (int d : divisors[a]) {
                    if (data.count(d) && data[d].count(m)) {
                        current += data[d][m];
                    }
                }
            }
            ans += current;

            for (int d : divisors[a]) {
                data[q][d]++;
            }
        }

        cout << ans << '\n';
    }

    return 0;
}

#include <bits/stdc++.h>
using namespace std;

const int days_in_month[] = {31, 29, 31, 30, 31, 30, 31, 31, 30, 31, 30, 31};

long long convert_to_seconds(const string& date, const string& time) {
    int year, month, day, hours, minutes, seconds;
    sscanf(date.c_str(), "%d-%d-%d", &year, &month, &day);
    sscanf(time.c_str(), "%d:%d:%d", &hours, &minutes, &seconds);

    int day_of_year = 0;
    for (int i = 0; i < month - 1; ++i) {
        day_of_year += days_in_month[i];
    }
    day_of_year += day;

    return (day_of_year - 1LL) * 86400 + hours * 3600LL + minutes * 60LL + seconds;
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int n, m;
    cin >> n >> m;
    cin.ignore(numeric_limits<streamsize>::max(), '\n');

    vector<long long> times;
    string line;

    while (getline(cin, line)) {
        if (line.empty()) continue;
        string timestamp = line.substr(0, 19);
        string date = timestamp.substr(0, 10);
        string time = timestamp.substr(11, 8);
        long long t = convert_to_seconds(date, time);
        times.push_back(t);
    }

    deque<long long> dq;
    long long answer = -1;

    for (long long t : times) {
        dq.push_back(t);
        while (!dq.empty() && dq.front() < t - n) {
            dq.pop_front();
        }
        if (dq.size() >= m) {
            answer = t;
            break;
        }
    }

    if (answer == -1) {
        cout << -1 << endl;
        return 0;
    }

    long long total_seconds = answer;
    int days = total_seconds / 86400;
    int remaining_seconds = total_seconds % 86400;

    int day_of_year = days + 1;
    int month = 1, day;
    for (int i = 0; i < 12; ++i) {
        if (day_of_year <= days_in_month[i]) {
            month = i + 1;
            day = day_of_year;
            break;
        }
        day_of_year -= days_in_month[i];
    }

    int hours = remaining_seconds / 3600;
    remaining_seconds %= 3600;
    int minutes = remaining_seconds / 60;
    int seconds = remaining_seconds % 60;

    cout << "2012-"
         << setw(2) << setfill('0') << month << "-"
         << setw(2) << setfill('0') << day << " "
         << setw(2) << setfill('0') << hours << ":"
         << setw(2) << setfill('0') << minutes << ":"
         << setw(2) << setfill('0') << seconds << endl;

    return 0;
}

#include <bits/stdc++.h>
using namespace std;

struct StateResult {
    bool can_win;
    int score;
};

map<string, StateResult> memo;
map<string, int> num_map;

int calculate_points(const string& s) {
    int sum = 0;
    for (char c : s) sum += (c - 'a' + 1);
    return sum * num_map[s];
}

StateResult dp(const string& s) {
    if (memo.count(s)) return memo[s];

    vector<pair<char, bool>> moves;
    for (char c = 'a'; c <= 'z'; ++c) {
        string left = string(1, c) + s;
        if (num_map.count(left)) moves.emplace_back(c, true);
        string right = s + string(1, c);
        if (num_map.count(right)) moves.emplace_back(c, false);
    }

    if (moves.empty()) return memo[s] = {false, 0};

    bool can_win = false;
    int best_score = -1e9;

    for (auto [c, is_left] : moves) {
        string new_s = is_left ? (string(1, c) + s) : (s + string(1, c));
        auto [opp_win, opp_score] = dp(new_s);
        int points = calculate_points(new_s);

        if (!opp_win) {
            if (!can_win || (points - opp_score) > best_score) {
                can_win = true;
                best_score = points - opp_score;
            }
        } else {
            if (!can_win && (points - opp_score) > best_score) {
                best_score = points - opp_score;
            }
        }
    }

    return memo[s] = {can_win, best_score};
}

int main() {
    int n; cin >> n;
    vector<string> words(n);
    for (int i = 0; i < n; ++i) cin >> words[i];

    num_map.clear();
    for (string& word : words) {
        set<string> substrs;
        int len = word.size();
        for (int i = 0; i < len; ++i)
            for (int j = i; j < len; ++j)
                substrs.insert(word.substr(i, j - i + 1));
        for (const string& s : substrs) num_map[s]++;
    }

    vector<string> initials;
    for (auto& [s, cnt] : num_map)
        if (s.size() == 1) initials.push_back(s);

    bool first_win = false;
    int best_score = -1e9;
    string best_s;

    for (string s : initials) {
        auto [win, score] = dp(s);
        if (win) {
            if (!first_win || score > best_score) {
                first_win = true;
                best_score = score;
                best_s = s;
            }
        } else {
            if (!first_win && score > best_score) {
                best_score = score;
                best_s = s;
            }
        }
    }

    auto [win, score_diff] = dp(best_s);
    int first = (score_diff + best_score) / 2;
    int second = (best_score - score_diff) / 2;

    cout << (win ? "First" : "Second") << endl;
    if (win) {
        cout << (score_diff + calculate_points(best_s)) << " " << calculate_points(best_s) - score_diff << endl;
    } else {
        int total = best_score;
        int first_p = (total + score_diff) / 2;
        int second_p = (total - score_diff) / 2;
        cout << first_p << " " << second_p << endl;
    }
}

