using namespace std;

int main() {
    int n, m;
    cin >> n >> m;

    vector<vector<int>> answers(n, vector<int>(m));
    for (int i = 0; i < n; ++i) {
        for (int j = 0; j < m; ++j) {
            cin >> answers[i][j];
        }
    }

    int max_truth_tellers = 0;
    int min_liars = n;

    for (int i = 0; i < (1 << n); ++i) { // Iterate through all possible subsets of truth-tellers
        vector<bool> is_truth_teller(n, false);
        int num_truth_tellers = 0;
        for (int j = 0; j < n; ++j) {
            if ((i >> j) & 1) {
                is_truth_teller[j] = true;
                num_truth_tellers++;
            }
        }

        bool consistent = true;
        for (int j = 0; j < m; ++j) { // Check consistency for each question
            vector<int> truth_values;
            bool found_truth_value = false;

            for (int k = 0; k < n; ++k) {
                if (is_truth_teller[k] && answers[k][j] != 0) {
                    if (!found_truth_value) {
                        truth_values.push_back(answers[k][j]);
                        found_truth_value = true;
                    } else {
                        if (truth_values[0] != answers[k][j]) {
                            consistent = false;
                            break;
                        }
                    }
                }
            }

            if (!consistent) break;

            if (found_truth_value) {
                for (int k = 0; k < n; ++k) {
                    if (!is_truth_teller[k] && answers[k][j] != 0) {
                        if (answers[k][j] == truth_values[0]) {
                            consistent = false;
                            break;
                        }
                    }
                }
            }

            if (!consistent) break;
        }

        if (consistent) {
            if (num_truth_tellers > max_truth_tellers) {
                max_truth_tellers = num_truth_tellers;
                min_liars = n - num_truth_tellers;
            } else if (num_truth_tellers == max_truth_tellers) {
                min_liars = min(min_liars, n - num_truth_tellers);
            }
        }
    }

    cout << min_liars << endl;

    return 0;
}

