#include <bits/stdc++.h>
#include "dungeons.h"
#include <vector>
#include <algorithm>
using namespace std;

const int MAX_LEVEL = 20; // 2^20 steps is sufficient for the constraints

struct Lift {
    int jump;
    long long sum_p;
    int min_s;
};

vector<vector<Lift>> lifts;
vector<long long> sum_win;
vector<int> s, p, w, l;
int n;

void init(int N, vector<int> S, vector<int> P, vector<int> W, vector<int> L) {
    n = N;
    s = S;
    p = P;
    w = W;
    l = L;

    // Precompute sum_win for each dungeon
    sum_win.resize(n + 1, 0);
    for (int i = n - 1; i >= 0; --i) {
        if (W[i] == n) {
            sum_win[i] = s[i];
        } else {
            sum_win[i] = s[i] + sum_win[W[i]];
        }
    }

    // Initialize binary lifting table for loss paths
    lifts.resize(MAX_LEVEL, vector<Lift>(n));
    // Level 0 (2^0 = 1 step)
    for (int i = 0; i < n; ++i) {
        lifts[0][i] = {L[i], P[i], S[i]};
    }
    // Higher levels (2^k steps)
    for (int k = 1; k < MAX_LEVEL; ++k) {
        for (int i = 0; i < n; ++i) {
            Lift prev = lifts[k-1][i];
            if (prev.jump == n) {
                lifts[k][i] = {n, prev.sum_p, prev.min_s};
            } else {
                Lift next = lifts[k-1][prev.jump];
                lifts[k][i] = {
                    next.jump,
                    prev.sum_p + next.sum_p,
                    min(prev.min_s, next.min_s)
                };
            }
        }
    }
}

long long simulate(int x, int z) {
    long long strength = z;
    int current = x;
    while (current != n) {
        if (strength >= s[current]) {
            strength += sum_win[current];
            current = n;
        } else {
            long long added_p = 0;
            int temp_current = current;
            // Use binary lifting to accumulate as many steps as possible
            for (int k = MAX_LEVEL - 1; k >= 0; --k) {
                if (temp_current == n) break;
                Lift step = lifts[k][temp_current];
                if (strength + added_p + step.sum_p < step.min_s) {
                    added_p += step.sum_p;
                    temp_current = step.jump;
                }
            }
            // Check if after jumps we can win
            if (temp_current != n && strength + added_p >= s[temp_current]) {
                strength += added_p;
                strength += s[temp_current];
                current = w[temp_current];
            } else {
                strength += added_p;
                current = temp_current;
                // Take individual steps until we can win or reach the end
                while (current != n && strength < s[current]) {
                    strength += p[current];
                    current = l[current];
                }
                if (current != n) {
                    strength += s[current];
                    current = w[current];
                }
            }
        }
    }
    return strength;
}

#include <bits/stdc++.h>
using namespace std;

int main() {
    int n, b, q;
    cin >> n >> b >> q;
    vector<pair<int, int>> hints;

    bool has_b = false;
    for (int i = 0; i < q; ++i) {
        int u, qu;
        cin >> u >> qu;
        if (u == b) {
            if (qu != n) {
                cout << "unfair" << endl;
                return 0;
            }
            has_b = true;
        }
        hints.emplace_back(u, qu);
    }
    if (!has_b) {
        hints.emplace_back(b, n);
    }

    sort(hints.begin(), hints.end());
    vector<pair<int, int>> sorted_hints;
    for (size_t i = 0; i < hints.size(); ++i) {
        if (i > 0 && hints[i].first == sorted_hints.back().first) {
            if (hints[i].second != sorted_hints.back().second) {
                cout << "unfair" << endl;
                return 0;
            }
            continue;
        }
        sorted_hints.push_back(hints[i]);
    }

    sorted_hints.insert(sorted_hints.begin(), {0, 0});

    for (size_t i = 1; i < sorted_hints.size(); ++i) {
        if (sorted_hints[i].first < sorted_hints[i-1].first) {
            cout << "unfair" << endl;
            return 0;
        }
        if (sorted_hints[i].second < sorted_hints[i-1].second) {
            cout << "unfair" << endl;
            return 0;
        }
    }

    vector<int> count_r(5);
    for (int r = 0; r < 5; ++r) {
        if (r == 0) {
            count_r[r] = b / 5;
        } else {
            if (r > b) {
                count_r[r] = 0;
            } else {
                count_r[r] = (b - r) / 5 + 1;
            }
        }
        if (count_r[r] < n / 5) {
            cout << "unfair" << endl;
            return 0;
        }
    }

    for (size_t i = 1; i < sorted_hints.size(); ++i) {
        int prev_u = sorted_hints[i-1].first;
        int prev_q = sorted_hints[i-1].second;
        int curr_u = sorted_hints[i].first;
        int curr_q = sorted_hints[i].second;
        int delta = curr_q - prev_q;
        if (delta < 0) {
            cout << "unfair" << endl;
            return 0;
        }

        int sum_min = 0, sum_max = 0;
        for (int r = 0; r < 5; ++r) {
            int a_prev;
            if (r == 0) {
                a_prev = prev_u / 5;
            } else {
                if (prev_u < r) a_prev = 0;
                else a_prev = (prev_u - r) / 5 + 1;
            }

            int a_curr;
            if (r == 0) {
                a_curr = curr_u /5;
            } else {
                if (curr_u < r) a_curr =0;
                else a_curr = (curr_u - r)/5 +1;
            }

            int count_in_interval = a_curr - a_prev;
            int c_max = count_r[r] - a_curr;
            int b_min = max(0, (n/5) - (a_prev + c_max));
            int b_max = min(count_in_interval, n/5);

            sum_min += b_min;
            sum_max += b_max;
        }

        if (delta < sum_min || delta > sum_max) {
            cout << "unfair" << endl;
            return 0;
        }
    }

    cout << "fair" << endl;
    return 0;
}

