using namespace std;

int maximum_teams(int N, int K, vector<int> L) {
    sort(L.begin(), L.end());
    int count = 0;
    int right = N - 1;
    while (right >= 2) {
        int current = L[right];
        int target = K - current;
        auto it = lower_bound(L.begin(), L.begin() + right + 1, target + 1);
        int left = it - L.begin();
        if (left <= right) {
            int window_size = right - left + 1;
            if (window_size >= 3) {
                int triplets = window_size / 3;
                count += triplets;
                right = left - 1;
            } else {
                right--;
            }
        } else {
            right--;
        }
    }
    return count;
}

