#include "fish.h"

#include <vector>
#include <algorithm>
#include <map>

using namespace std;

struct EvenColumn {
    vector<int> ys;
    vector<long long> prefix;
};

long long max_weights(int N, int M, vector<int> X, vector<int> Y, vector<int> W) {
    vector<vector<pair<int, int>>> even_cols(N);
    for (int i = 0; i < M; ++i) {
        int x = X[i];
        even_cols[x].emplace_back(Y[i] + 1, W[i]);
    }

    vector<EvenColumn> even_data(N);
    for (int x = 0; x < N; x += 2) {
        auto &col = even_cols[x];
        sort(col.begin(), col.end(), [](const pair<int, int> &a, const pair<int, int> &b) {
            return a.first < b.first;
        });

        vector<int> ys;
        vector<long long> prefix;
        long long sum = 0;
        for (auto &p : col) {
            ys.push_back(p.first);
            sum += p.second;
            prefix.push_back(sum);
        }
        even_data[x].ys = move(ys);
        even_data[x].prefix = move(prefix);
    }

    map<int, long long> previous_dp;
    previous_dp[0] = 0;

    for (int c = 1; c < N; c += 2) {
        map<int, long long> current_dp;

        int even_left = c - 1;
        int even_right = c + 1;

        const EvenColumn *left_data = nullptr;
        if (even_left >= 0) {
            left_data = &even_data[even_left];
        }

        const EvenColumn *right_data = nullptr;
        if (even_right < N) {
            right_data = &even_data[even_right];
        }

        int max_right = 0;
        if (right_data && !right_data->ys.empty()) {
            max_right = right_data->ys.back();
        }

        for (const auto &entry : previous_dp) {
            int prev_max = entry.first;
            long long sum_so_far = entry.second;

            int current_max = max(prev_max, max_right);

            long long sum_left = 0;
            if (left_data && !left_data->ys.empty()) {
                int threshold = max(prev_max, current_max);
                auto it = upper_bound(left_data->ys.begin(), left_data->ys.end(), threshold);
                int count = it - left_data->ys.begin();
                if (count > 0) {
                    sum_left = left_data->prefix[count - 1];
                }
            }

            long long sum_right = 0;
            if (right_data && !right_data->ys.empty()) {
                auto it = upper_bound(right_data->ys.begin(), right_data->ys.end(), current_max);
                int count = it - right_data->ys.begin();
                if (count > 0) {
                    sum_right = right_data->prefix[count - 1];
                }
            }

            long long total = sum_so_far + sum_left + sum_right;

            if (current_dp.count(current_max)) {
                if (total > current_dp[current_max]) {
                    current_dp[current_max] = total;
                }
            } else {
                current_dp[current_max] = total;
            }
        }

        previous_dp.swap(current_dp);
    }

    long long ans = 0;
    for (const auto &entry : previous_dp) {
        ans = max(ans, entry.second);
    }

    return ans;
}

