#include <bits/stdc++.h>
using namespace std;

using ll = long long;

ll gcd(ll a, ll b) { return b ? gcd(b, a % b) : a; }

vector<ll> divisors(ll x) {
    vector<ll> res;
    for (ll i = 1; i * i <= x; ++i) {
        if (x % i == 0) {
            res.push_back(i);
            if (i != x / i) res.push_back(x / i);
        }
    }
    sort(res.rbegin(), res.rend());
    return res;
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    int n, q;
    cin >> n >> q;

    vector<int> a(n), b(n), c(n);
    for (auto& x : a) cin >> x;
    for (auto& x : b) cin >> x;
    for (auto& x : c) cin >> x;

    ll G = 0;
    for (int i = 0; i < n; ++i) {
        ll prod = ll(a[i]) * b[i];
        G = gcd(G, prod);
    }

    vector<ll> divs = divisors(G);

    vector<ll> valid_a, valid_b;
    vector<ll> cost_a, cost_b;

    for (ll g : divs) {
        bool ok = true;
        ll ca = 0, cb = 0;
        for (int i = 0; i < n; ++i) {
            bool a_ok = (a[i] % g == 0);
            bool b_ok = (b[i] % g == 0);
            if (!a_ok && !b_ok) {
                ok = false;
                break;
            }
            if (!a_ok) ca += c[i];
            if (!b_ok) cb += c[i];
        }
        if (ok) {
            valid_a.push_back(g);
            cost_a.push_back(ca);
            valid_b.push_back(g);
            cost_b.push_back(cb);
        }
    }

    vector<pair<ll, ll>> a_list;
    for (size_t i = 0; i < valid_a.size(); ++i) {
        a_list.emplace_back(cost_a[i], valid_a[i]);
    }
    sort(a_list.begin(), a_list.end(), [&](auto& x, auto& y) {
        return x.second > y.second;
    });

    vector<pair<ll, ll>> b_list;
    for (size_t i = 0; i < valid_b.size(); ++i) {
        b_list.emplace_back(cost_b[i], valid_b[i]);
    }
    sort(b_list.begin(), b_list.end());

    vector<pair<ll, ll>> b_prefix;
    ll max_g = 0;
    for (auto [cost, g] : b_list) {
        if (b_prefix.empty() || cost > b_prefix.back().first) {
            max_g = max(max_g, g);
            b_prefix.emplace_back(cost, max_g);
        } else if (cost == b_prefix.back().first) {
            if (g > b_prefix.back().second) {
                b_prefix.back().second = g;
                max_g = g;
            }
        }
    }

    while (q--) {
        ll d;
        cin >> d;

        ll max_sum = 0;

        for (auto [ca, ga] : a_list) {
            if (ca > d) continue;
            ll rem = d - ca;

            auto it = upper_bound(b_prefix.begin(), b_prefix.end(), make_pair(rem, LLONG_MAX));
            if (it == b_prefix.begin()) continue;
            --it;
            ll gb = it->second;
            max_sum = max(max_sum, ga + gb);

            if (ga + gb >= valid_a[0] + valid_b.back()) break;
        }

        cout << max_sum << " ";
    }

    return 0;
}

#include <bits/stdc++.h>
using namespace std;

struct DSU {
    vector<int> parent, size;
    vector<bool> is_cycle;

    DSU(int n) : parent(n), size(n, 1), is_cycle(n, false) {
        iota(parent.begin(), parent.end(), 0);
    }

    int find(int u) {
        if (parent[u] != u)
            parent[u] = find(parent[u]);
        return parent[u];
    }

    bool unite(int u, int v, bool &cycle) {
        int pu = find(u), pv = find(v);
        cycle = false;
        if (pu == pv) {
            if (!is_cycle[pu]) {
                is_cycle[pu] = true;
                cycle = true;
            }
            return false;
        } else {
            parent[pu] = pv;
            size[pv] += size[pu];
            is_cycle[pv] = is_cycle[pu] || is_cycle[pv];
            cycle = is_cycle[pv];
            return true;
        }
    }
};

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int n, m, q;
    cin >> n >> m >> q;

    DSU dsu(n + m + 1);
    long long total = 0;

    int offset = n + 1;
    vector<bool> row_used(n + 1, false), col_used(m + 1, false);
    int rows = 0, cols = 0;

    while (q--) {
        int x, y;
        char c;
        cin >> x >> y >> c;

        int u, v;
        if (c == 'R') {
            u = x;
            v = offset + y;
            if (!row_used[x]) {
                row_used[x] = true;
                rows++;
            }
        } else {
            u = offset + y;
            v = x;
            if (!col_used[y]) {
                col_used[y] = true;
                cols++;
            }
        }

        bool cycle;
        bool merged = dsu.unite(u, v, cycle);
        if (merged) {
            if (cycle) {
                total += (long long)dsu.size[dsu.find(u)] * dsu.size[dsu.find(u)];
            }
        } else {
            if (cycle) {
                total += (long long)dsu.size[dsu.find(u)] * dsu.size[dsu.find(u)];
            }
        }

        cout << total << '\n';
    }

    return 0;
}

