#include <bits/stdc++.h>
using namespace std;

void solve() {
    int n, k;
    string a, b;
    cin >> n >> k >> a >> b;

    // Check if the number of 1s is the same
    int cnt_a = count(a.begin(), a.end(), '1');
    int cnt_b = count(b.begin(), b.end(), '1');
    if (cnt_a != cnt_b) {
        cout << "-1\n";
        return;
    }

    // Handle k=0 case
    if (k == 0) {
        if (a == b) cout << "0\n";
        else cout << "-1\n";
        return;
    }

    int m = cnt_a;
    if (m == 0) {
        if (a == b) cout << "0\n";
        else cout << "-1\n";
        return;
    }

    if (k > m) {
        cout << "-1\n";
        return;
    }

    // If m == k, check reversing entire string
    if (m == k) {
        reverse(a.begin(), a.end());
        if (a == b) {
            cout << "1\n1 " << n << "\n";
        } else {
            cout << "-1\n";
        }
        return;
    }

    // Handle k=1 case
    if (k == 1) {
        vector<int> a_pos, b_pos;
        for (int i = 0; i < n; ++i) {
            if (a[i] == '1') a_pos.push_back(i);
            if (b[i] == '1') b_pos.push_back(i);
        }
        if (a_pos.size() != b_pos.size()) {
            cout << "-1\n";
            return;
        }
        vector<pair<int, int>> steps;
        vector<char> current_a(a.begin(), a.end());
        for (int i = (int)a_pos.size() - 1; i >= 0; --i) {
            int current = a_pos[i];
            int target = b_pos[i];
            if (current == target) continue;
            int l = min(current, target);
            int r = max(current, target);
            steps.emplace_back(l + 1, r + 1);
            reverse(current_a.begin() + l, current_a.begin() + r + 1);
            a_pos[i] = target;
        }
        if (string(current_a.begin(), current_a.end()) == b) {
            cout << steps.size() << "\n";
            for (auto& p : steps) {
                cout << p.first << " " << p.second << "\n";
            }
        } else {
            cout << "-1\n";
        }
        return;
    }

    // For other k values, not handled in this code
    cout << "-1\n";
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    int t;
    cin >> t;
    while (t--) solve();

    return 0;
}

