    getline(cin, s);

    vector<Region> regions;
    int len = s.size();
    int i = 0, start = 0;

    while (i < len) {
        if (s[i] == '(') {
            if (start < i) {
                regions.push_back({false, s.substr(start, i - start)});
            }
            int j = i + 1;
            while (j < len && s[j] != ')') ++j;
            if (j < len) {
                regions.push_back({true, s.substr(i + 1, j - i - 1)});
            }
            i = j + 1;
            start = i;
        } else {
            ++i;
        }
    }

    if (start < len) {
        regions.push_back({false, s.substr(start, len - start)});
    }

    int max_out = 0, count_in = 0;

    for (const auto& r : regions) {
        int current = 0;
        for (char c : r.str) {
            if (isalpha(c)) {
                ++current;
            } else {
                if (current > 0) {
                    if (r.is_inside) ++count_in;
                    else if (current > max_out) max_out = current;
                    current = 0;
                }
            }
        }
        if (current > 0) {
            if (r.is_inside) ++count_in;
            else if (current > max_out) max_out = current;
        }
    }

    cout << max_out << " " << count_in << endl;

