}

#include <bits/stdc++.h>
using namespace std;

const int INF = 1e9;

int main() {
    string s;
    int k;
    cin >> s >> k;
    int n = s.size();
    vector<vector<int>> cost(n, vector<int>(n, 0));

    for (int i = n - 1; i >= 0; --i) {
        for (int j = i + 1; j < n; ++j) {
            if (s[i] == s[j]) {
                cost[i][j] = cost[i + 1][j - 1];
            } else {
                cost[i][j] = 1 + cost[i + 1][j - 1];
            }
        }
    }

    int k_max = min(k, n);
    vector<vector<int>> dp(k_max + 1, vector<int>(n + 1, INF));
    vector<vector<int>> parent(k_max + 1, vector<int>(n + 1, -1));

    for (int i = 1; i <= n; ++i) {
        dp[1][i] = cost[0][i - 1];
    }

    for (int j = 2; j <= k_max; ++j) {
        for (int i = j; i <= n; ++i) {
            int min_val = INF;
            int best_m = -1;
            for (int m = j - 1; m < i; ++m) {
                if (dp[j - 1][m] + cost[m][i - 1] < min_val) {
                    min_val = dp[j - 1][m] + cost[m][i - 1];
                    best_m = m;
                }
            }
            dp[j][i] = min_val;
            parent[j][i] = best_m;
        }
    }

    int min_changes = INF;
    int best_j = 1;
    for (int j = 1; j <= k_max; ++j) {
        if (dp[j][n] < min_changes) {
            min_changes = dp[j][n];
            best_j = j;
        }
    }

    vector<int> splits;
    int current_j = best_j;
    int current_i = n;
    while (current_j > 1) {
        int m = parent[current_j][current_i];
        splits.push_back(m);
        current_i = m;
        current_j--;
    }
    reverse(splits.begin(), splits.end());

    vector<int> split_points = {0};
    for (int m : splits) split_points.push_back(m);
    split_points.push_back(n);

    vector<string> parts;
    for (int i = 0; i < split_points.size() - 1; ++i) {
        int start = split_points[i];
        int end = split_points[i + 1] - 1;
        string part = s.substr(start, end - start + 1);
        for (int a = 0; a < (part.size() + 1) / 2; ++a) {
            int b = part.size() - 1 - a;
            if (part[a] != part[b]) part[b] = part[a];
        }
        parts.push_back(part);
    }

    cout << min_changes << '\n';
    for (size_t i = 0; i < parts.size(); ++i) {
        if (i > 0) cout << '+';
        cout << parts[i];
    }
    cout << '\n';

    return 0;
}

#include <bits/stdc++.h>
#include "supertrees.h"
#include <vector>
#include <algorithm>
#include <queue>
using namespace std;

int construct(vector<vector<int>> p) {
    int n = p.size();
    vector<vector<int>> answer(n, vector<int>(n, 0));

    // Check diagonal elements are 1
    for (int i = 0; i < n; ++i) {
        if (p[i][i] != 1) {
            return 0;
        }
    }

    // Check symmetry and valid values (0, 1, 2)
    for (int i = 0; i < n; ++i) {
        for (int j = 0; j < n; ++j) {
            if (p[i][j] != p[j][i]) {
                return 0;
            }
            if (p[i][j] < 0 || p[i][j] > 2) {
                return 0;
            }
        }
    }

    // Find components where p[i][j] > 0
    vector<bool> visited(n, false);
    vector<int> component;
    vector<vector<bool>> comp(n, vector<bool>(n, false));

    for (int i = 0; i < n; ++i) {
        if (!visited[i]) {
            queue<int> q;
            q.push(i);
            visited[i] = true;
            component.clear();
            component.push_back(i);
            while (!q.empty()) {
                int u = q.front();
                q.pop();
                for (int v = 0; v < n; ++v) {
                    if (v != u && p[u][v] > 0 && !visited[v]) {
                        visited[v] = true;
                        q.push(v);
                        component.push_back(v);
                    }
                }
            }
            // Check that all pairs in component have p > 0
            for (int u : component) {
                for (int v : component) {
                    if (u != v && p[u][v] == 0) {
                        return 0;
                    }
                }
            }
            // Check for 2-clique condition
            for (int u : component) {
                vector<int> twos;
                for (int v : component) {
                    if (u != v && p[u][v] == 2) {
                        twos.push_back(v);
                    }
                }
                // Check all pairs in twos have p == 2
                for (int a : twos) {
                    for (int b : twos) {
                        if (a != b && p[a][b] != 2) {
                            return 0;
                        }
                    }
                }
            }
            // Build the component
            if (component.size() == 1) {
                continue;
            }
            int root = *min_element(component.begin(), component.end());
            for (int u : component) {
                if (u != root) {
                    answer[root][u] = 1;
                    answer[u][root] = 1;
                }
            }
