vector<int> prefix_aA, prefix_aT, prefix_aC;
vector<int> prefix_bA, prefix_bT, prefix_bC;
vector<int> prefix_mismatch;

void init(string a, string b) {
    int n = a.size();
    prefix_aA.assign(n + 1, 0);
    prefix_aT.assign(n + 1, 0);
    prefix_aC.assign(n + 1, 0);
    prefix_bA.assign(n + 1, 0);
    prefix_bT.assign(n + 1, 0);
    prefix_bC.assign(n + 1, 0);
    prefix_mismatch.assign(n + 1, 0);

    for (int i = 0; i < n; ++i) {
        prefix_aA[i + 1] = prefix_aA[i] + (a[i] == 'A');
        prefix_aT[i + 1] = prefix_aT[i] + (a[i] == 'T');
        prefix_aC[i + 1] = prefix_aC[i] + (a[i] == 'C');

        prefix_bA[i + 1] = prefix_bA[i] + (b[i] == 'A');
        prefix_bT[i + 1] = prefix_bT[i] + (b[i] == 'T');
        prefix_bC[i + 1] = prefix_bC[i] + (b[i] == 'C');

        prefix_mismatch[i + 1] = prefix_mismatch[i] + (a[i] != b[i]);
    }
}

int get_distance(int x, int y) {
    int aA = prefix_aA[y + 1] - prefix_aA[x];
    int aT = prefix_aT[y + 1] - prefix_aT[x];
    int aC = prefix_aC[y + 1] - prefix_aC[x];

    int bA = prefix_bA[y + 1] - prefix_bA[x];
    int bT = prefix_bT[y + 1] - prefix_bT[x];
    int bC = prefix_bC[y + 1] - prefix_bC[x];

    if (aA != bA || aT != bT || aC != bC) {
        return -1;
    }

    int m = prefix_mismatch[y + 1] - prefix_mismatch[x];
    return (m + 1) / 2;
}

