using namespace std;

vector<int> malfunctioning_cores(int N) {
    vector<int> ans(N, 0);
    vector<int> single_res(N, -2);
    int count_zero = 0;
    vector<int> zeros;

    for (int i = 0; i < N; ++i) {
        vector<int> T = {i};
        single_res[i] = run_diagnostic(T);
        if (single_res[i] == 1) {
            ans[i] = 1;
            return ans;
        }
        if (single_res[i] == 0) {
            count_zero++;
            zeros.push_back(i);
        }
    }

    if (count_zero == 2) {
        ans[zeros[0]] = 1;
        ans[zeros[1]] = 1;
        return ans;
    }

    vector<int> candidates;
    for (int i = 0; i < N; ++i) {
        candidates.push_back(i);
    }

    function<vector<int>(vector<int>)> find_malfunctioning = [&](vector<int> S) {
        if (S.empty()) return vector<int>();
        if (S.size() == 1) {
            vector<int> T = {S[0]};
            int res = run_diagnostic(T);
            if (res == 1) {
                return vector<int>(1, S[0]);
            } else {
                return vector<int>();
            }
        }

        int mid = S.size() / 2;
        vector<int> left(S.begin(), S.begin() + mid);
        vector<int> right(S.begin() + mid, S.end());

        int res_left = run_diagnostic(left);
        vector<int> mal_left;
        if (res_left == 1) {
            mal_left = find_malfunctioning(left);
        } else if (res_left == 0) {
            mal_left = left;
        } else {
            vector<int> complement;
            for (int x : S) {
                if (find(left.begin(), left.end(), x) == left.end()) {
                    complement.push_back(x);
                }
            }
            int res_comp = run_diagnostic(complement);
            if (res_comp == 1) {
                mal_left = find_malfunctioning(complement);
            } else if (res_comp == 0) {
                mal_left = complement;
            } else {
                mal_left = find_malfunctioning(right);
            }
        }

        vector<int> remaining;
        for (int x : S) {
            if (find(mal_left.begin(), mal_left.end(), x) == mal_left.end()) {
                remaining.push_back(x);
            }
        }

        vector<int> mal_right = find_malfunctioning(remaining);
        mal_left.insert(mal_left.end(), mal_right.begin(), mal_right.end());
        return mal_left;
    };

    vector<int> mal = find_malfunctioning(candidates);
    for (int x : mal) {
        ans[x] = 1;
    }

    return ans;
}

