int biggest_stadium(int N, vector<vector<int>> F) {
    int total_empty = 0;
    for (int r = 0; r < N; ++r) {
        for (int c = 0; c < N; ++c) {
            total_empty += (F[r][c] == 0);
        }
    }

    // Precompute up and down for columns
    vector<vector<int>> up(N, vector<int>(N, 0));
    vector<vector<int>> down(N, vector<int>(N, 0));
    for (int c = 0; c < N; ++c) {
        up[c][0] = (F[0][c] == 0) ? 1 : 0;
        for (int r = 1; r < N; ++r) {
            if (F[r][c] == 0) {
                up[c][r] = up[c][r-1] + 1;
            } else {
                up[c][r] = 0;
            }
        }
        down[c][N-1] = (F[N-1][c] == 0) ? 1 : 0;
        for (int r = N-2; r >= 0; --r) {
            if (F[r][c] == 0) {
                down[c][r] = down[c][r+1] + 1;
            } else {
                down[c][r] = 0;
            }
        }
    }

    // Compute max_row
    int max_row = 0;
    for (int R = 0; R < N; ++R) {
        int sum = 0;
        for (int c = 0; c < N; ++c) {
            if (F[R][c] == 1) continue;
            int span = up[c][R] + down[c][R] - 1;
            sum += span;
        }
        max_row = max(max_row, sum);
    }

    // Precompute left and right for rows
    vector<vector<int>> left(N, vector<int>(N, 0));
    vector<vector<int>> right(N, vector<int>(N, 0));
    for (int r = 0; r < N; ++r) {
        left[r][0] = (F[r][0] == 0) ? 1 : 0;
        for (int c = 1; c < N; ++c) {
            if (F[r][c] == 0) {
                left[r][c] = left[r][c-1] + 1;
            } else {
                left[r][c] = 0;
            }
        }
        right[r][N-1] = (F[r][N-1] == 0) ? 1 : 0;
        for (int c = N-2; c >= 0; --c) {
            if (F[r][c] == 0) {
                right[r][c] = right[r][c+1] + 1;
            } else {
                right[r][c] = 0;
            }
        }
    }

    // Compute max_col
    int max_col = 0;
    for (int C = 0; C < N; ++C) {
        int sum = 0;
        for (int r = 0; r < N; ++r) {
            if (F[r][C] == 1) continue;
            int span = left[r][C] + right[r][C] - 1;
            sum += span;
        }
        max_col = max(max_col, sum);
    }

    return max(max_row, max_col);
}

