    vector<int> l(n);
    for (int& li : l) cin >> li;

    vector<ll> pow_m1(5001);
    for (int i = 0; i <= 5000; ++i)
        pow_m1[i] = mod_pow(m-1, i, p);

    ll prev_s = 0, prev_ns = 0;
    for (int i = 0; i < n; ++i) {
        int li = l[i];
        if (li == 1) {
            ll curr_s = ((prev_s * (m-1)) % p + (prev_ns * m) % p) % p;
            if (i == 0) curr_s = m % p;
            prev_ns = 0;
            prev_s = curr_s;
        } else {
            ll A = (m % p) * pow_m1[li-1] % p;
            if (i == 0) {
                prev_ns = A;
                prev_s = 0;
                continue;
            }
            ll from_s = prev_s * A % p;
            ll from_ns = prev_ns * A % p;
            if (li >= 2 && prev_ns > 0) {
                from_ns = (from_ns - prev_ns % p * 1 % p + p) % p;
            }
            prev_ns = (from_s + from_ns) % p;
            prev_s = 0;
        }
    }

    cout << (prev_s + prev_ns) % p << '\n';

