int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    ll Sf, Pf, Gf;
    cin >> Sf >> Pf >> Gf;

    int N;
    cin >> N;

    vector<pair<ll, ll>> bottles(1); // 1-based index
    int singles = 0;
    map<pair<ll, ll>, int> cnt;
    ll pairs = 0;

    for (int i = 0; i < N; ++i) {
        char cmd;
        cin >> cmd;

        if (cmd == 'A') {
            ll s, p, g;
            cin >> s >> p >> g;

            ll u = s * Pf - p * Sf;
            ll v = s * Gf - g * Sf;

            bottles.emplace_back(u, v);

            if (u == 0 && v == 0) {
                ++singles;
            } else {
                auto key = make_pair(u, v);
                auto neg = make_pair(-u, -v);
                pairs += cnt[neg];
                cnt[key]++;
            }
        } else {
            int r;
            cin >> r;
            auto [u, v] = bottles[r];

            if (u == 0 && v == 0) {
                --singles;
            } else {
                auto key = make_pair(u, v);
                auto neg = make_pair(-u, -v);
                cnt[key]--;
                pairs -= cnt[neg];
            }
        }

        if (singles > 0) {
            cout << "1\n";
        } else if (pairs > 0) {
            cout << "2\n";
        } else {
            cout << "0\n";
        }
    }

    return 0;
}

