struct Fenwick {
    vector<int> tree;
    int size;

    Fenwick(int n) : tree(n + 2), size(n) {}

    void update(int idx, int delta) {
        for (; idx <= size; idx += idx & -idx)
            tree[idx] += delta;
    }

    int query(int idx) {
        int res = 0;
        for (; idx > 0; idx -= idx & -idx)
            res += tree[idx];
        return res;
    }

    int queryGreater(int x) {
        return query(size) - query(x);
    }
};

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int N;
    cin >> N;

    vector<int> A(N + 1), B(N + 1);
    for (int i = 1; i <= N; ++i) {
        cin >> A[i];
    }
    for (int i = 1; i <= N; ++i) {
        cin >> B[i];
    }

    Fenwick fenA(1e5), fenB(1e5);
    for (int i = 1; i <= N; ++i) {
        fenA.update(A[i], 1);
        fenB.update(B[i], 1);
    }

    int Q;
    cin >> Q;

    while (Q--) {
        int type, k;
        char c;
        cin >> type;
        if (type == 1 || type == 2) {
            cin >> k >> c;
            Fenwick &fen = (type == 1) ? fenA : fenB;
            vector<int> &arr = (type == 1) ? A : B;
            int old_val = arr[k];
            int new_val = old_val + (c == '+' ? 1 : -1);
            fen.update(old_val, -1);
            fen.update(new_val, 1);
            arr[k] = new_val;
        } else {
            cin >> k;
            int currentA = A[k];
            int currentB = B[k];
            int sumA = fenA.queryGreater(currentA);
            int sumB = fenB.queryGreater(currentB);
            int T = sumA + sumB;

            int count = 0;
            for (int i = 1; i <= N; ++i) {
                int sa = fenA.queryGreater(A[i]);
                int sb = fenB.queryGreater(B[i]);
                if (sa + sb < T) {
                    ++count;
                }
            }
            cout << count + 1 << '\n';
        }
    }

    return 0;
}

