    map<string, int> original_counts;
    map<string, int> sorted_counts;

    for (int i = 0; i < n; ++i) {
        cin >> original[i];
        string s = original[i];
        sort(s.begin(), s.end());
        sorted[i] = s;
        original_counts[original[i]]++;
        sorted_counts[s]++;
    }

    long long total = 0;
    long long zero_ops = 0;
    long long one_ops = 0;
    long long impossible = 0;

    // Calculate zero_ops: pairs of identical original strings
    for (auto &[s, cnt] : original_counts) {
        zero_ops += (long long)cnt * (cnt - 1) / 2;
    }

    // Calculate one_ops: pairs with same sorted string but different original
    for (auto &[s, cnt] : sorted_counts) {
        one_ops += (long long)cnt * (cnt - 1) / 2;
    }
    one_ops -= zero_ops; // subtract pairs already counted in zero_ops

    // Total pairs is n*(n-1)/2
    long long total_pairs = (long long)n * (n - 1) / 2;
    impossible = total_pairs - zero_ops - one_ops;

    total = zero_ops * 0 + one_ops * 1 + impossible * 1337;
    cout << total << '\n';

    return 0;
}

