#include <bits/stdc++.h>
using namespace std;

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    int N;
    cin >> N;
    vector<vector<int>> adj(N + 1);
    for (int i = 0; i < N - 1; ++i) {
        int a, b;
        cin >> a >> b;
        adj[a].push_back(b);
        adj[b].push_back(a);
    }

    vector<int> parent(N + 1);
    vector<bool> visited(N + 1);
    queue<int> q;
    q.push(1);
    visited[1] = true;
    while (!q.empty()) {
        int u = q.front();
        q.pop();
        for (int v : adj[u]) {
            if (!visited[v]) {
                visited[v] = true;
                parent[v] = u;
                q.push(v);
            }
        }
    }

    vector<int> post_order;
    fill(visited.begin(), visited.end(), false);
    function<void(int)> dfs = [&](int u) {
        visited[u] = true;
        for (int v : adj[u]) {
            if (v != parent[u] && !visited[v]) {
                dfs(v);
            }
        }
        post_order.push_back(u);
    };
    dfs(1);

    vector<int> match(N + 1, 0);
    for (int u : post_order) {
        if (u == 1) continue;
        if (match[u] == 0 && match[parent[u]] == 0) {
            match[u] = parent[u];
            match[parent[u]] = u;
        }
    }

    vector<int> v(N + 1);
    vector<int> remaining;
    for (int i = 1; i <= N; ++i) {
        if (match[i] != 0) {
            v[i] = match[i];
        } else {
            remaining.push_back(i);
        }
    }

    int k = remaining.size();
    if (k > 0) {
        for (int i = 0; i < k; ++i) {
            int next = (i + 1) % k;
            v[remaining[i]] = remaining[next];
        }
    }

    int sum = 0;
    vector<int> depth(N + 1);
    fill(visited.begin(), visited.end(), false);
    q.push(1);
    visited[1] = true;
    depth[1] = 0;
    while (!q.empty()) {
        int u = q.front();
        q.pop();
        for (int v_node : adj[u]) {
            if (!visited[v_node]) {
                visited[v_node] = true;
                depth[v_node] = depth[u] + 1;
                q.push(v_node);
            }
        }
    }

    auto lca = [&](int a, int b) {
        while (a != b) {
            if (depth[a] > depth[b]) {
                a = parent[a];
            } else {
                b = parent[b];
            }
        }
        return a;
    };

    auto distance = [&](int a, int b) {
        int ancestor = lca(a, b);
        return depth[a] + depth[b] - 2 * depth[ancestor];
    };

    for (int i = 1; i <= N; ++i) {
        sum += distance(i, v[i]);
    }

    cout << sum << '\n';
    for (int i = 1; i <= N; ++i) {
        cout << v[i] << (i < N ? ' ' : '\n');
    }

    return 0;
}

