using namespace std;

variant<bool, vector<int>> find_journey(int N, int M, vector<int> U, vector<int> V) {
    if (N < 3) {
        return false;
    }

    vector<int> outgoing_from_zero;
    for (int i = 0; i < M; ++i) {
        if (U[i] == 0) {
            outgoing_from_zero.push_back(i);
        }
    }

    if (outgoing_from_zero.size() < 2) {
        return false;
    }

    int A = outgoing_from_zero[0];
    int a = V[A];
    int B = -1;
    for (int i = 0; i < M; ++i) {
        if (U[i] == a && V[i] == 0) {
            B = i;
            break;
        }
    }

    int C = outgoing_from_zero[1];
    int c = V[C];
    int D = -1;
    for (int i = 0; i < M; ++i) {
        if (U[i] == c && V[i] == 0) {
            D = i;
            break;
        }
    }

    if (B == -1 || D == -1) {
        return false;
    }

    return vector<int>{A, B, C, D, B, A, D, C};
}

