using namespace std;

int main() {
    string s;
    cin >> s;
    int n = s.size();
    int c0 = count(s.begin(), s.end(), '0');
    int c1 = n - c0;
    int required_sum = (c0 * c1) / 2;

    vector<vector<int>> dp(c0 + 1, vector<int>(required_sum + 1, -1));
    dp[0][0] = 0;

    for (int i = n - 1; i >= 0; --i) {
        vector<vector<int>> new_dp(c0 + 1, vector<int>(required_sum + 1, -1));
        for (int k_prev = 0; k_prev <= c0; ++k_prev) {
            for (int s_prev = 0; s_prev <= required_sum; ++s_prev) {
                if (dp[k_prev][s_prev] == -1) continue;
                // Option 1: include current position as 0
                if (k_prev + 1 <= c0) {
                    int new_k = k_prev + 1;
                    int added = (n - i - 1) - k_prev;
                    int new_s = s_prev + added;
                    if (new_s <= required_sum) {
                        int overlap = dp[k_prev][s_prev] + (s[i] == '0');
                        if (new_dp[new_k][new_s] < overlap) {
                            new_dp[new_k][new_s] = overlap;
                        }
                    }
                }
                // Option 2: exclude current position
                if (new_dp[k_prev][s_prev] < dp[k_prev][s_prev]) {
                    new_dp[k_prev][s_prev] = dp[k_prev][s_prev];
                }
            }
        }
        dp = move(new_dp);
    }

    int max_overlap = dp[c0][required_sum];
    cout << (c0 - max_overlap) << endl;
    return 0;
}

