using ll = long long;
const int MOD = 998244353;

ll modpow(ll a, ll b) {
    ll res = 1;
    while (b) {
        if (b % 2) res = res * a % MOD;
        a = a * a % MOD;
        b /= 2;
    }
    return res;
}

int main() {
    int n, m;
    cin >> n >> m;
    if (m == 0) {
        cout << 0 << endl;
        return 0;
    }
    if (m == 1) {
        cout << (n == 2 ? 0 : 0) << endl;
        return 0;
    }
    vector<ll> dp_prev(m, 1);
    for (int i = 1; i < n; ++i) {
        vector<ll> dp_curr(m, 0);
        vector<ll> prefix(m);
        prefix[0] = dp_prev[0];
        for (int j = 1; j < m; ++j)
            prefix[j] = (prefix[j-1] + dp_prev[j]) % MOD;
        for (int j = 0; j < m; ++j) {
            int k = m - j -1;
            if (k >=0) {
                if (k < m)
                    dp_curr[j] = prefix[k];
                else
                    dp_curr[j] = prefix[m-1];
            }
        }
        dp_prev = move(dp_curr);
    }
    ll total = 0;
    for (int j = 0; j < m; ++j) {
        int allowed = m - j -1;
        if (allowed <0) allowed = 0;
        total = (total + dp_prev[j] * allowed) % MOD;
    }
    cout << total << endl;
    return 0;
}

