using namespace std;

bool tryAddToEnds(int x, vector<int>& A, vector<int>& B) {
    if (are_connected({x}, {A.front()})) {
        A.insert(A.begin(), x);
        return true;
    }
    if (are_connected({x}, {A.back()})) {
        A.push_back(x);
        return true;
    }
    if (!B.empty()) {
        if (are_connected({x}, {B.front()})) {
            B.insert(B.begin(), x);
            return true;
        }
        if (are_connected({x}, {B.back()})) {
            B.push_back(x);
            return true;
        }
    }
    return false;
}

void mergeAandB(vector<int>& A, vector<int>& B) {
    if (B.empty()) return;
    bool merged = false;
    if (are_connected({A.back()}, {B.front()})) {
        A.insert(A.end(), B.begin(), B.end());
        merged = true;
    } else if (are_connected({A.back()}, {B.back()})) {
        reverse(B.begin(), B.end());
        A.insert(A.end(), B.begin(), B.end());
        merged = true;
    } else if (are_connected({A.front()}, {B.front()})) {
        reverse(A.begin(), A.end());
        A.insert(A.end(), B.begin(), B.end());
        merged = true;
    } else if (are_connected({A.front()}, {B.back()})) {
        reverse(B.begin(), B.end());
        vector<int> newA = B;
        newA.insert(newA.end(), A.begin(), A.end());
        A.swap(newA);
        merged = true;
    } else {
        vector<int> allA(A.begin(), A.end());
        vector<int> allB(B.begin(), B.end());
        if (are_connected(allA, allB)) {
            A.insert(A.end(), B.begin(), B.end());
            merged = true;
        } else {
            reverse(B.begin(), B.end());
            A.insert(A.end(), B.begin(), B.end());
            merged = true;
        }
    }
    B.clear();
}

vector<int> longest_trip(int N, int D) {
    if (N == 0) return {};
    vector<int> A, B;
    A.push_back(0);
    if (N == 1) return A;
    B.push_back(1);

    for (int x = 2; x < N; ++x) {
        bool added = tryAddToEnds(x, A, B);
        if (added) continue;
        if (!B.empty()) {
            mergeAandB(A, B);
        }
        added = tryAddToEnds(x, A, B);
        if (!added) {
            if (are_connected({x}, {A.front()})) {
                A.insert(A.begin(), x);
            } else if (are_connected({x}, {A.back()})) {
                A.push_back(x);
            } else {
                A.push_back(x);
            }
        }
    }

    if (!B.empty()) {
        if (are_connected(A, B)) {
            mergeAandB(A, B);
        } else {
            if (B.size() > A.size()) {
                A.swap(B);
            }
        }
    }

    if (A.size() >= 3) {
        vector<int> tmp;
        tmp.push_back(A[0]);
        for (size_t i = 1; i < A.size(); ++i) {
            if (are_connected({tmp.back()}, {A[i]})) {
                tmp.push_back(A[i]);
            } else {
                vector<int> checkLeft(tmp.begin(), tmp.end());
                vector<int> checkRight(A.begin() + i, A.end());
                if (are_connected(checkLeft, checkRight)) {
                    tmp.insert(tmp.end(), A.begin() + i, A.end());
                    break;
                } else {
                    tmp = vector<int>(A.begin() + i, A.end());
                    break;
                }
            }
        }
        if (tmp.size() > A.size()) {
            A.swap(tmp);
        }
    }

    return A;
}

