#include <bits/stdc++.h>
using namespace std;

using ll = long long;

template<typename T>
struct MonotonicStack {
    stack<pair<T, int>> st;
    T sum;
    MonotonicStack() : sum(0) {}
    void push(T val, int pos) {
        int cnt = 1;
        while (!st.empty() && val <= st.top().first) {
            cnt += st.top().second;
            sum -= st.top().first * st.top().second;
            st.pop();
        }
        sum += val * cnt;
        st.emplace(val, cnt);
    }
    void clear() {
        while (!st.empty()) st.pop();
        sum = 0;
    }
};

ll compute_sum_res(const vector<int>& prefix_sum) {
    int n = prefix_sum.size() - 1;
    ll sum_res = 0;
    vector<int> arr(n);
    for (int i = 1; i <= n; ++i) arr[i-1] = prefix_sum[i];
    vector<int> left(n), right(n);
    stack<int> s;
    for (int i = 0; i < n; ++i) {
        while (!s.empty() && arr[s.top()] >= arr[i]) s.pop();
        left[i] = s.empty() ? -1 : s.top();
        s.push(i);
    }
    s = stack<int>();
    for (int i = n-1; i >= 0; --i) {
        while (!s.empty() && arr[s.top()] > arr[i]) s.pop();
        right[i] = s.empty() ? n : s.top();
        s.push(i);
    }
    for (int i = 0; i < n; ++i) {
        int l = left[i] + 1;
        int r = right[i] - 1;
        ll cnt = (i - l + 1) * 1LL * (r - i + 1);
        sum_res += cnt * arr[i];
    }
    vector<ll> pre(n+1);
    for (int i = 0; i < n; ++i) pre[i+1] = pre[i] + arr[i];
    ll total = 0;
    for (int i = 1; i <= n; ++i) total += pre[i];
    sum_res = total - sum_res;
    return sum_res;
}

ll compute_sum_open_close(const vector<int>& prefix_sum) {
    int n = prefix_sum.size() - 1;
    vector<ll> pre(n+1);
    for (int i = 0; i <= n; ++i) pre[i] = prefix_sum[i];
    vector<ll> spre(n+1);
    for (int i = 0; i <= n; ++i) spre[i] = (i == 0) ? pre[i] : spre[i-1] + pre[i];
    ll sum = 0;
    for (int j = 1; j <= n; ++j) {
        sum += pre[j] * j;
        sum -= spre[j-1];
    }
    return sum;
}

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int t;
    cin >> t;
    while (t--) {
        int n;
        string s;
        cin >> n >> s;

        vector<int> prefix_sum(n+1, 0);
        for (int i = 0; i < n; ++i) {
            prefix_sum[i+1] = prefix_sum[i] + (s[i] == '(' ? 1 : -1);
        }

        ll sum_res = compute_sum_res(prefix_sum);
        ll sum_open_close = compute_sum_open_close(prefix_sum);
        ll sum_cost_a = 2 * sum_res + sum_open_close;

        vector<int> cnt_open(n+1), cnt_close(n+1);
        for (int i = 0; i < n; ++i) {
            cnt_open[i+1] = cnt_open[i] + (s[i] == '(');
            cnt_close[i+1] = cnt_close[i] + (s[i] == ')');
        }

        ll subtract = 0;
        for (int i = 0; i < n; ++i) {
            MonotonicStack<int> mstack;
            int x = prefix_sum[i];
            ll current = 0;
            for (int j = i+1; j <= n; ++j) {
                mstack.push(prefix_sum[j], j);
                if ((j - i) % 2 != 0) continue;
                int m = j - i;
                int open = cnt_open[j] - cnt_open[i];
                int close = cnt_close[j] - cnt_open[i];
                if (open != close) continue;
                current += (x - mstack.sum);
            }
            subtract += current;
        }

        cout << sum_cost_a << '\n';
    }
}

