#include "robot.h"
#include <array>
#include <functional>

using namespace std;

const int WEST = 1, SOUTH = 2, EAST = 3, NORTH = 4;

void program_pulibot() {
    // Handle end cell detection (south and east are boundary)
    for (int s0 : {0, WEST, SOUTH, EAST, NORTH}) {
        for (int s1 : {-2, -1, 0}) { // S[1] (west)
            for (int s4 : {-2, -1, 0}) { // S[4] (north)
                array<int, 5> S = {s0, s1, -2, -2, s4};
                set_instruction(S, 1, 'T');
            }
        }
    }

    // Exploration phase: S[0] == 0
    for (int s1 : {-2, -1, 0}) { // west
        for (int s2 : {-2, -1, 0}) { // south
            for (int s3 : {-2, -1, 0}) { // east
                for (int s4 : {-2, -1, 0}) { // north
                    array<int, 5> S = {0, s1, s2, s3, s4};
                    if (s1 == 0) {
                        set_instruction(S, WEST, 'W');
                    } else if (s2 == 0) {
                        set_instruction(S, SOUTH, 'S');
                    } else if (s3 == 0) {
                        set_instruction(S, EAST, 'E');
                    } else if (s4 == 0) {
                        set_instruction(S, NORTH, 'N');
                    }
                }
            }
        }
    }

    // Backtracking phase: S[0] is WEST (1), SOUTH (2), EAST (3), NORTH (4)
    array<pair<int, char>, 4> backtrack = {{{WEST, 'E'}, {SOUTH, 'N'}, {EAST, 'W'}, {NORTH, 'S'}}};
    for (auto [dir, action] : backtrack) {
        for (int s1 : {-2, -1, 0, 1, 2, 3, 4}) { // west
            for (int s2 : {-2, -1, 0, 1, 2, 3, 4}) { // south
                for (int s3 : {-2, -1, 0, 1, 2, 3, 4}) { // east
                    for (int s4 : {-2, -1, 0, 1, 2, 3, 4}) { // north
                        array<int, 5> S = {dir, s1, s2, s3, s4};
                        // Check if this is not the end cell (already handled)
                        if (S[2] != -2 || S[3] != -2) {
                            set_instruction(S, 0, action);
                        }
                    }
                }
            }
        }
    }
}

#include <bits/stdc++.h>
using namespace std;

bool checkA(int X, const vector<int>& a, int k) {
    int count = 0;
    int parity = 1; // 1 for odd, 2 for even
    for (int num : a) {
        if (parity == 1) {
            count++;
            parity = 2;
        } else {
            if (num <= X) {
                count++;
                parity = 1;
            }
        }
        if (count >= k) return true;
    }
    return count >= k;
}

bool checkB(int X, const vector<int>& a, int k) {
    int count = 0;
    int parity = 1; // 1 for odd (needs <=X), 2 for even
    for (int num : a) {
        if (parity == 1) {
            if (num <= X) {
                count++;
                parity = 2;
            }
        } else {
            count++;
            parity = 1;
        }
        if (count >= k) return true;
    }
    return count >= k;
}

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);

    int n, k;
    cin >> n >> k;
    vector<int> a(n);
    for (int& x : a) cin >> x;

    int low = *min_element(a.begin(), a.end());
    int high = *max_element(a.begin(), a.end());
    int ans = high;

    while (low <= high) {
        int mid = low + (high - low) / 2;
        if (checkA(mid, a, k) || checkB(mid, a, k)) {
            ans = mid;
            high = mid - 1;
        } else {
            low = mid + 1;
        }
    }

    cout << ans << '\n';
    return 0;
}

