const int MOD = 1000000007;

int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);

    int n, m, k;
    cin >> n >> m >> k;

    unordered_map<int, vector<int>> edges;

    bool invalid = false;
    for (int i = 0; i < m; ++i) {
        int u, v;
        cin >> u >> v;
        if (v - u <= k && v != u + 1) {
            invalid = true;
        }
        edges[u].push_back(v);
    }

    if (invalid) {
        cout << "0\n";
        return 0;
    }

    for (auto& p : edges) {
        sort(p.second.begin(), p.second.end());
    }

    int c = 0;
    int max_u = n - (k + 1);
    if (k + 1 <= n - 1) {
        for (int u = 1; u <= max_u; ++u) {
            int v = u + k + 1;
            if (edges.find(u) == edges.end()) {
                ++c;
            } else {
                auto& vec = edges[u];
                auto it = lower_bound(vec.begin(), vec.end(), v);
                if (it == vec.end() || *it != v) {
                    ++c;
                }
            }
        }
    }

    long long result = 1;
    long long power = 2;
    for (int p = c; p > 0; p >>= 1) {
        if (p & 1) {
            result = (result * power) % MOD;
        }
        power = (power * power) % MOD;
    }

    cout << result << "\n";

    return 0;
}

