using namespace std;

string a_str, b_str;

void init(string a, string b) {
    a_str = a;
    b_str = b;
}

int get_distance(int x, int y) {
    string s = a_str.substr(x, y - x + 1);
    string t = b_str.substr(x, y - x + 1);

    array<int, 3> count_s = {0, 0, 0}; // A, T, C
    array<int, 3> count_t = {0, 0, 0};
    array<array<int, 3>, 3> count = {0}; // count[c1][c2] tracks transitions from c1 to c2

    for (char c : s) {
        if (c == 'A') count_s[0]++;
        else if (c == 'T') count_s[1]++;
        else count_s[2]++;
    }
    for (char c : t) {
        if (c == 'A') count_t[0]++;
        else if (c == 'T') count_t[1]++;
        else count_t[2]++;
    }

    if (count_s != count_t) {
        return -1;
    }

    int m_total = 0;
    for (int i = 0; i < s.size(); ++i) {
        char s_char = s[i];
        char t_char = t[i];
        int s_idx, t_idx;
        s_idx = (s_char == 'A' ? 0 : (s_char == 'T' ? 1 : 2));
        t_idx = (t_char == 'A' ? 0 : (t_char == 'T' ? 1 : 2));
        count[s_idx][t_idx]++;
        if (s_char != t_char) m_total++;
    }

    int num_2_cycles = 0;
    // Check pairs (A, T)
    num_2_cycles += min(count[0][1], count[1][0]);
    // Check pairs (A, C)
    num_2_cycles += min(count[0][2], count[2][0]);
    // Check pairs (T, C)
    num_2_cycles += min(count[1][2], count[2][1]);

    int m_remaining = m_total - 2 * num_2_cycles;

    if (m_remaining == 0) {
        return num_2_cycles;
    } else if (m_remaining == 3) {
        return num_2_cycles + 2;
    } else {
        return -1;
    }
}

#include <iostream>
#include <vector>
#include <algorithm>

using namespace std;

int main() {
  int n, k;
  cin >> n >> k;

  vector<vector<char>> moves(4, vector<char>(k));
  for (int i = 0; i < 4; ++i) {
    for (int j = 0; j < k; ++j) {
      cin >> moves[i][j];
    }
  }

  vector<vector<int>> board(n, vector<int>(n, 0));
  vector<pair<int, int>> positions = {
    {0, 0}, {0, n - 1}, {n - 1, n - 1}, {n - 1, 0}
  };

  for (int move_index = 0; move_index < k; ++move_index) {
    for (int player = 0; player < 4; ++player) {
      int row = positions[player].first;
      int col = positions[player].second;

      board[row][col] = player + 1;

      char move = moves[player][move_index];
      int new_row = row;
      int new_col = col;

      if (move == 'N') {
        new_row--;
      } else if (move == 'E') {
        new_col++;
      } else if (move == 'S') {
        new_row++;
      } else { 
        new_col--;
      }

      if (new_row < 0 || new_row >= n || new_col < 0 || new_col >= n) {
        continue;
      }

      bool collision = false;
      for (int other_player = 0; other_player < 4; ++other_player) {
        if (other_player != player && positions[other_player].first == new_row && positions[other_player].second == new_col) {
          collision = true;
          break;
        }
      }

      if (collision) {
        continue;
      }

      positions[player].first = new_row;
      positions[player].second = new_col;
      board[new_row][new_col] = player + 1;
    }
  }

  vector<int> counts(4, 0);
  for (int i = 0; i < n; ++i) {
    for (int j = 0; j < n; ++j) {
      if (board[i][j] != 0) {
        counts[board[i][j] - 1]++;
      }
    }
  }

  int max_count = 0;
  for (int count : counts) {
    max_count = max(max_count, count);
  }

  vector<int> winners;
  for (int i = 0; i < 4; ++i) {
    if (counts[i] == max_count) {
      winners.push_back(i + 1);
    }
  }

  if (winners.empty()) {
    cout << "No" << endl;
  } else {
    cout << winners.size() << " " << max_count << endl;
    sort(winners.begin(), winners.end());
    for (int winner : winners) {
      cout << winner << endl;
    }
  }

  return 0;
}

