    vector<int> log_table(n + 1);
    log_table[0] = 0;
    for (int i = 1; i <= n; ++i) {
        log_table[i] = log_table[i / 2] + 1;
    }
    int max_level = log_table[n] + 1;
    vector<vector<int>> st(max_level, vector<int>(n));

    for (int i = 0; i < n; ++i) {
        st[0][i] = i;
