            int b = st[k - 1][i + (1 << (k - 1))];
            st[k][i] = (s[a] <= s[b]) ? a : b;
        }
    }

    vector<int> pos[26];
    for (int i = 0; i < n; ++i) {
        pos[s[i] - 'a'].push_back(i);
    }

    vector<char> selected;
    int current_end = 0;

    while (current_end <= n - m) {
        int L = current_end;
        int R = current_end + m - 1;
        if (R >= n) R = n - 1;
        int len = R - L + 1;
        int k = log_table[len];
        int a = st[k][L];
        int b = st[k][R - (1 << k) + 1];
        int min_pos = (s[a] <= s[b]) ? a : b;
        char c = s[min_pos];
        vector<int>& v = pos[c - 'a'];

        int best = -1;
        int low = 0, high = v.size() - 1;
        while (low <= high) {
            int mid = (low + high) / 2;
            if (v[mid] > R) {
                high = mid - 1;
            } else if (v[mid] < L) {
                low = mid + 1;
            } else {
                best = mid;
                low = mid + 1;
            }
        }
        if (best == -1) {
            continue;
        }
        int selected_pos = v[best];
        selected.push_back(c);
        current_end = selected_pos + 1;
    }

    sort(selected.begin(), selected.end());
    for (char c : selected) {
        cout << c;
    }
    cout << '\n';

    return 0;
}

#include <vector>
#include <algorithm>
#include <climits>
#include <iostream>
using namespace std;

struct Camera {
    int t, s;
};

vector<int> getHackTimes(int T, const vector<Camera>& cams) {
    vector<pair<int, int>> intervals;
    for (auto& cam : cams) {
        int a = max(0, T - cam.s);
        int b = T - 1;
        if (a > b) return {};
        intervals.emplace_back(a, b);
    }
    sort(intervals.begin(), intervals.end(), [](const auto& a, const auto& b) {
        return a.second < b.second;
    });
    vector<int> hackTimes;
    int last = -1;
    for (auto& [a, b] : intervals) {
        int hack = max(a, last + 1);
        if (hack > b) return {};
        hackTimes.push_back(hack);
        last = hack;
    }
    return hackTimes;
}

int main() {
    int n;
    cin >> n;
    vector<Camera> cameras(n);
    for (int i = 0; i < n; ++i) {
        cin >> cameras[i].t >> cameras[i].s;
    }

    vector<Camera> first_group, second_group;
    for (auto& cam : cameras) {
        if (cam.t == 1 || cam.t == 3) first_group.push_back(cam);
        if (cam.t == 2 || cam.t == 3) second_group.push_back(cam);
    }

    int ans = INT_MAX;

    if (first_group.empty()) {
        vector<pair<int, int>> intervals;
        for (auto& cam : second_group) {
            int a = 0, b = -1;
            bool ok = false;
            for (int T2 = 0; T2 <= 2 * n; ++T2) {
                a = max(0, T2 - cam.s);
                b = T2 - 1;
                if (a <= b) {
                    intervals.emplace_back(a, b);
                    ok = true;
                    break;
                }
            }
            if (!ok) {
                cout << -1 << endl;
                return 0;
            }
        }
        sort(intervals.begin(), intervals.end(), [](const auto& a, const auto& b) {
            return a.second < b.second;
        });
        int last = -1;
        for (auto& [a, b] : intervals) {
            int hack = max(a, last + 1);
            if (hack > b) {
                cout << -1 << endl;
                return 0;
            }
            last = hack;
        }
        cout << last + 1 + 1 << endl;
        return 0;
    }

    for (int T1 = 0; T1 <= 2 * n; ++T1) {
        vector<int> hackTimes = getHackTimes(T1, first_group);
        if (hackTimes.empty()) continue;

        vector<Camera> phase2_cams;
        for (auto& cam : second_group) {
            bool covered = false;
            if (cam.t == 3) {
                for (size_t i = 0; i < first_group.size(); ++i) {
                    if (&cam == &first_group[i] && hackTimes[i] + cam.s >= T1) {
                        covered = (hackTimes[i] + cam.s >= T1);
                        break;
                    }
                }
            }
            if (!covered) phase2_cams.push_back(cam);
        }

        int K = phase2_cams.size();
        int T2 = T1 + K;
        vector<pair<int, int>> intervals;
        for (auto& cam : phase2_cams) {
            int a = max(T1, T2 - cam.s);
            int b = T2 - 1;
            if (a > b) goto next_T1;
            intervals.emplace_back(a, b);
        }
        sort(intervals.begin(), intervals.end(), [](const auto& a, const auto& b) {
            return a.second < b.second;
        });
        int last_hack = -1;
        for (auto& [a, b] : intervals) {
            int hack = max(a, last_hack + 1);
            if (hack > b) goto next_T1;
            last_hack = hack;
        }

        if (T2 + 1 < ans) ans = T2 + 1;

        next_T1:;
    }

    cout << (ans != INT_MAX ? ans : -1) << endl;
    return 0;
}

