typedef long long ll;
const int MOD = 1e9+7;
ll inv(ll a, ll m) {
    return a <= 1 ? a : m - (m / a) * inv(m % a, m) % m;
}

int n;
vector<vector<int>> adj;
vector<int> parent, depth;

void dfs(int u, int p) {
    parent[u] = p;
    for (int v : adj[u]) {
        if (v != p) {
            depth[v] = depth[u] + 1;
            dfs(v, u);
        }
    }
}

vector<int> get_path(int u, int v) {
    vector<int> path_u, path_v;
    while (u != v) {
        if (depth[u] > depth[v]) {
            path_u.push_back(u);
            u = parent[u];
        } else {
            path_v.push_back(v);
            v = parent[v];
        }
    }
    path_u.push_back(u);
    reverse(path_v.begin(), path_v.end());
    path_u.insert(path_u.end(), path_v.begin(), path_v.end());
    return path_u;
}

int compute_subtree_size(int u, int p, int block) {
    int size = 1;
    for (int v : adj[u]) {
        if (v != p && v != block) {
            size += compute_subtree_size(v, u, block);
        }
    }
    return size;
}

