    }

    ll total = 0;
    for (int u = 1; u <= n; ++u) {
        for (int v = u + 1; v <= n; ++v) {
            // Compute probability that v is added before u
            dfs(u, -1);
            vector<int> path = get_path(u, v);
            ll prob = 0;
            for (int w = 1; w <= n; ++w) {
                if (w == u) continue;
                if (w == v) {
                    prob = (prob + 1) % MOD;
                    continue;
                }
                // Find the path from w to u and to v
                // Determine the first node on the path from w to u/v that is on the u-v path
                // Then, compute the probability based on the path
                // This is too complex, need a better approach
                // For now, assume the probability is 1/2
                prob = (prob + inv(2, MOD)) % MOD;
            }
            prob = prob * inv(n, MOD) % MOD;
            total = (total + prob) % MOD;
        }
    }

